INSERT INTO `cms` VALUES (1026, 195, 'Vòng loại cuộc thi BWD-SICT 2019 &quot;Best Website Design for Student Information and Communication Technology&quot;', 'Vòng loại cuộc thi BWD-SICT 2019 &quot;Best Website Design for Student Information and Communication Technology&quot;', 'Vòng loại cuộc thi BWD-SICT 2019 &quot;Best Website Design for Student Information and Communication Technology&quot;', 'vong-loai-cuoc-thi-bwd-sict-2019--best-website-design-for-student-information-and-communication-technology-', '', 'Vòng loại cuộc thi BWD-SICT 2019 &quot;Best Website Design for Student Information and Communication Technology&quot;', '', '2019/06/10/avatar.jpg', 'Nhằm mục đích tạo sân chơi, phát động phong trào để các bạn sinh viên đang theo học tại 2 đơn vị đào tạo chủ lực về công nghệ thông tin: Khoa Công nghệ Thông tin và Truyền thông và Trường CĐ Công nghệ Thông tin thuộc Đại học Đà nẵng, có cơ hội cùng tham gia vào các hoạt động phát triển ngồn nhân lực chất lượng cao đáp ứng nhu cầu xã hội trong thời đại công nghệ 4.0', '', '&lt;p&gt;Nhằm mục đ&amp;iacute;ch tạo s&amp;acirc;n chơi, ph&amp;aacute;t động phong tr&amp;agrave;o để c&amp;aacute;c bạn sinh vi&amp;ecirc;n đang theo học tại 2 đơn vị đ&amp;agrave;o tạo chủ lực về c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin: Khoa C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng v&amp;agrave; Trường CĐ C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin thuộc Đại học Đ&amp;agrave; nẵng, c&amp;oacute; cơ hội c&amp;ugrave;ng tham gia v&amp;agrave;o c&amp;aacute;c hoạt động ph&amp;aacute;t triển ngồn nh&amp;acirc;n lực chất lượng cao đ&amp;aacute;p ứng nhu cầu x&amp;atilde; hội trong thời đại c&amp;ocirc;ng nghệ 4.0&lt;/p&gt;\r\n&lt;p&gt;&lt;img class=&quot;rao&quot; src=&quot;http://vku.udn.vn//uploads/2020/06/07/1591530574-1557457090-5.jpg&quot; alt=&quot;&quot; width=&quot;100%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;Cuộc thi BWD-SICT &amp;ldquo;Best Website Design for Student Information and Communication Technology&amp;rdquo; kh&amp;ocirc;ng chỉ gi&amp;uacute;p cho c&amp;aacute;c bạn sinh vi&amp;ecirc;n được ph&amp;aacute;t triển l&amp;ograve;ng đam m&amp;ecirc;, t&amp;iacute;nh s&amp;aacute;ng tạo trong việc x&amp;acirc;y dựng thiết kế website. C&amp;aacute;c t&amp;aacute;c phẩm c&amp;oacute; t&amp;iacute;nh s&amp;aacute;ng tạo, độc đ&amp;aacute;o sẽ được giới thiệu đến với cộng đồng mạng, đặc biệt l&amp;agrave; c&amp;aacute;c Doanh nghiệp c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin h&amp;agrave;ng đầu tại khu vực miền Trung.&lt;/p&gt;\r\n&lt;p&gt;&lt;img class=&quot;rao&quot; src=&quot;http://vku.udn.vn//uploads/2020/06/07/1591530575-1557457090-11.jpg&quot; alt=&quot;&quot; width=&quot;100%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;Sau 2 v&amp;ograve;ng sơ loại, Ban tổ chức đ&amp;atilde; đ&amp;aacute;nh gi&amp;aacute; v&amp;agrave; chọn ra được 11 đội được tham gia v&amp;agrave;o v&amp;ograve;ng chung kết trong cuối th&amp;aacute;ng 5/2019.&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Danh s&amp;aacute;ch 11 đội lọt v&amp;agrave;o v&amp;ograve;ng chung kết:&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;&lt;strong&gt;1) Website rozy&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;NGUYỄN HO&amp;Agrave;NG VŨ 18IT1&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;PHAN TH&amp;Agrave;NH ĐẠT 18IT1&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;2) Website Ph&amp;aacute;t Triển Ước Mơ Cho B&amp;eacute;&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Nguyễn Minh Hiếu 18IT1&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Nguyễn Trọng Qu&amp;yacute; Mạnh 18IT1&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;L&amp;ecirc; Phước Đại 18IT1&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;3) Website HitLife&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Ng&amp;ocirc; Mỹ Hạnh 18IT1&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Trần Thị Ngọc Anh 18IT5&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Nguyễn Thị Nguyệt Minh 18IT2&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;4) Website b&amp;aacute;n đồ uống&amp;nbsp;&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Trần Phước T&amp;iacute;n 18IT2&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Nguyễn Văn Sang 18IT2&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;L&amp;ecirc; Quang Nhật 18IT2&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;5) Website MLAX forum&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Nguyễn Trọng Nghĩa 18IT5&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;6) Website r&amp;egrave;n luyện kỹ năng l&amp;agrave;m việc nh&amp;oacute;m&amp;nbsp;&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Đỗ Văn Chiến 18IT2&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Nguyễn Thanh T&amp;acirc;m 18IT1&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;L&amp;ecirc; Thị Minh Ch&amp;acirc;u 18IT1&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;7) Website kh&amp;aacute;ch sạn&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Trần Dương Quang 18IT3&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Hồ Nguyễn Xu&amp;acirc;n T&amp;acirc;y 18IT3&lt;/p&gt;\r\n&lt;p&gt;&lt;em&gt;&lt;strong&gt;8) Website Love Pet : Cửa h&amp;agrave;ng mua b&amp;aacute;n v&amp;agrave; chăm s&amp;oacute;c th&amp;uacute; cưng&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;B&amp;ugrave;i Xu&amp;acirc;n Thiện 18IT3&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;B&amp;ugrave;i Xu&amp;acirc;n Tường 18IT3&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Huỳnh Quang Trung 18IT1&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;9) Website t&amp;igrave;m b&amp;atilde;i đỗ xe&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Ng&amp;ocirc; L&amp;ecirc; Ph&amp;uacute;c Nguy&amp;ecirc;n 17IT3&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Trương H&amp;ugrave;ynh Đức 17IT3&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Phan Ngọc Việt Anh 17IT3&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;10) website đặt ph&amp;ograve;ng kh&amp;aacute;ch sạn&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Nguyễn Duy Sỹ 18IT5&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Th&amp;aacute;i Thị Quỳnh Nga 18it5&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;11) Website theo d&amp;otilde;i sự ph&amp;aacute;t triển của trẻ em&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;L&amp;ecirc; Thị Thanh H&amp;agrave; 18IT1&amp;nbsp;&lt;/p&gt;\r\n&lt;p