INSERT INTO `cms` VALUES (1032, 195, 'Thông báo Cuộc thi Sinh viên giỏi Tiếng Anh 2018', 'Thông báo Cuộc thi Sinh viên giỏi Tiếng Anh 2018', 'Thông báo Cuộc thi Sinh viên giỏi Tiếng Anh 2018', 'thong-bao-cuoc-thi-sinh-vien-gioi-tieng-anh-2018', '', 'Thông báo Cuộc thi Sinh viên giỏi Tiếng Anh 2018', '', '2018/11/12/1591531199banner_etc.jpg', 'Cuộc thi nhằm giúp sinh viên có động cơ, ý thức, điều kiện thực hành và phát triển kỹ năng sử dụng tiếng Anh, góp phần vào định hướng đào tạo quốc tế hóa cho sinh viên Công nghệ thông tin và Truyền thông, Đại học Đà Nẵng và tạo sân chơi bổ ích, giúp sinh viên trang bị thêm kiến thức về cuộc Cách mạng công nghiệp lần thứ 4 đang diễn ra mạnh mẽ trên toàn thế giới.', '', '&lt;h3 class=&quot;MsoNormal&quot;&gt;&lt;strong&gt;MỤC Đ&amp;Iacute;CH V&amp;Agrave; &amp;Yacute; NGHĨA&lt;/strong&gt;&lt;/h3&gt;\r\n&lt;p class=&quot;MsoListParagraphCxSpFirst&quot; style=&quot;mso-list: l3 level1 lfo2;&quot;&gt;&lt;a name=&quot;_Hlk496880351&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-family: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol;&quot;&gt;&amp;middot;&lt;span style=&quot;font-variant-numeric: normal; font-stretch: normal; font-size: 7pt; line-height: normal;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;text-indent: -0.25in;&quot;&gt;Cuộc thi nhằm gi&amp;uacute;p sinh vi&amp;ecirc;n c&amp;oacute; động cơ, &amp;yacute; thức, điều kiện thực h&amp;agrave;nh v&amp;agrave; ph&amp;aacute;t triển kỹ năng sử dụng tiếng Anh, g&amp;oacute;p phần v&amp;agrave;o định hướng đ&amp;agrave;o tạo quốc tế h&amp;oacute;a cho sinh vi&amp;ecirc;n C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng, Đại học Đ&amp;agrave; Nẵng v&amp;agrave; tạo s&amp;acirc;n chơi bổ &amp;iacute;ch, gi&amp;uacute;p sinh vi&amp;ecirc;n trang bị th&amp;ecirc;m kiến thức về cuộc C&amp;aacute;ch mạng c&amp;ocirc;ng nghiệp lần thứ 4 đang diễn ra mạnh mẽ tr&amp;ecirc;n to&amp;agrave;n thế giới.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoListParagraphCxSpMiddle&quot; style=&quot;mso-list: l3 level1 lfo2;&quot;&gt;&lt;!--[if !supportLists]--&gt;&lt;span style=&quot;font-family: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol;&quot;&gt;&amp;middot;&lt;span style=&quot;font-variant-numeric: normal; font-stretch: normal; font-size: 7pt; line-height: normal;&quot;&gt;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;!--[endif]--&gt;Tạo cơ hội cho c&amp;aacute;c bạn sinh vi&amp;ecirc;n t&amp;igrave;m hiểu v&amp;agrave; đề xuất giải ph&amp;aacute;p trước thời cơ v&amp;agrave; th&amp;aacute;ch thức m&amp;agrave; C&amp;ocirc;ng nghiệp lần thứ 4 mang lại trong c&amp;aacute;c lĩnh vực li&amp;ecirc;n quan.&lt;/p&gt;\r\n&lt;p class=&quot;MsoListParagraphCxSpLast&quot; style=&quot;mso-list: l3 level1 lfo2;&quot;&gt;&lt;!--[if !supportLists]--&gt;&lt;span style=&quot;font-family: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol;&quot;&gt;&amp;middot;&lt;span style=&quot;font-variant-numeric: normal; font-stretch: normal; font-size: 7pt; line-height: normal;&quot;&gt;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;!--[endif]--&gt;L&amp;agrave; dịp để c&amp;aacute;c bạn sinh vi&amp;ecirc;n gặp gỡ, giao lưu v&amp;agrave; trao dồi c&amp;aacute;c kỹ năng mềm.&lt;/p&gt;\r\n&lt;p class=&quot;MsoListParagraphCxSpLast&quot; style=&quot;text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot; src=&quot;http://vku.udn.vn//uploads/2020/06/07/1591531199-banner_etc.jpg&quot; alt=&quot;&quot; width=&quot;100%&quot; /&gt;&lt;/p&gt;\r\n&lt;h3 class=&quot;MsoNormal&quot;&gt;&lt;strong&gt;CHỦ ĐỀ V&amp;Agrave; ĐỐI TƯỢNG THAM GIA&lt;/strong&gt;&lt;/h3&gt;\r\n&lt;p class=&quot;MsoListParagraphCxSpFirst&quot; style=&quot;mso-list: l4 level1 lfo4;&quot;&gt;&lt;!--[if !supportLists]--&gt;&lt;span style=&quot;font-family: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol;&quot;&gt;&amp;middot;&lt;span style=&quot