INSERT INTO `cms` VALUES (1094, 185, 'Đại học Đà Nẵng phối hợp với doanh nghiệp hỗ trợ sinh viên có hoàn cảnh khó khăn do ảnh hưởng của dịch Covid -19', 'Đại học Đà Nẵng phối hợp với doanh nghiệp hỗ trợ sinh viên có hoàn cảnh khó khăn do ảnh hưởng của dịch Covid -19', 'Đại học Đà Nẵng phối hợp với doanh nghiệp hỗ trợ sinh viên có hoàn cảnh khó khăn do ảnh hưởng của dịch Covid -19', 'dai-hoc-da-nang-phoi-hop-voi-doanh-nghiep-ho-tro-sinh-vien-co-hoan-canh-kho-khan-do-anh-huong-cua-dich-covid-19', '', 'Đại học Đà Nẵng phối hợp với doanh nghiệp hỗ trợ sinh viên có hoàn cảnh khó khăn do ảnh hưởng của dịch Covid -19', '', '2020/09/02/Hinh 1_1.jpg', 'Sáng ngày 01/9/2020, Đại học Đà Nẵng (ĐHĐN) phối hợp với Công ty TNHH MTV Phát triển Nhà Đa Phước và Câu lạc bộ (CLB) Golf G8 tổ chức trao quà hỗ trợ 100 sinh viên (SV) ĐHĐN gặp khó khăn do ảnh hưởng của dịch Covid-19 với tổng trị giá 300 triệu đồng.', '', '&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: bolder; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;S&amp;aacute;ng ng&amp;agrave;y 01/9/2020, Đại học Đ&amp;agrave; Nẵng (ĐHĐN) phối hợp với C&amp;ocirc;ng ty TNHH MTV Ph&amp;aacute;t triển Nh&amp;agrave; Đa Phước v&amp;agrave; C&amp;acirc;u lạc bộ (CLB) Golf G8 tổ chức trao qu&amp;agrave; hỗ trợ 100 sinh vi&amp;ecirc;n (SV) ĐHĐN gặp kh&amp;oacute; khăn do ảnh hưởng của dịch Covid-19 với tổng trị gi&amp;aacute; 300 triệu đồng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; height: 518px; width: 875px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/09/02/1599060806-hinh-201_1.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Chương tr&amp;igrave;nh hỗ trợ SV tại ĐHĐN&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;Tham dự chương tr&amp;igrave;nh c&amp;oacute; PGS.TS. Nguyễn Ngọc Vũ-Th&amp;agrave;nh ủy vi&amp;ecirc;n, B&amp;iacute; thư Đảng ủy, Gi&amp;aacute;m đốc ĐHĐN, &amp;Ocirc;ng Nguyễn Duy Minh- Th&amp;agrave;nh ủy vi&amp;ecirc;n, Chủ tịch Li&amp;ecirc;n đo&amp;agrave;n Lao động th&amp;agrave;nh phố (LĐLĐTP) Đ&amp;agrave; Nẵng.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 574px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/09/02/1599060807-hinh-202.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Đại diện doanh nghiệp trao tượng trưng hỗ trợ SV cho l&amp;atilde;nh đạo ĐHĐN&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;Ph&amp;aacute;t biểu tại Chương tr&amp;igrave;nh, &amp;Ocirc;ng Nguyễn Văn Sĩ Tiến-Thư k&amp;yacute; CLB Golf G8 đ&amp;atilde; ph&amp;aacute;t biểu mong muốn sự đ&amp;oacute;ng g&amp;oacute;p của doanh nghiệp sẽ hỗ trợ gi&amp;uacute;p SV ĐHĐN vượt qua kh&amp;oacute; khăn do dịch Covid-19 bước v&amp;agrave;o năm học mới.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 598px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/09/02/1599060808-hinh-203_1.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Gi&amp;aacute;m đốc ĐHĐN ph&amp;aacute;t biểu, c&amp;aacute;m ơn nh&amp;agrave; t&amp;agrave;i trợ&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;Thay mặt l&amp;atilde;nh đạo ĐHĐN, PGS.TS. Nguyễn Ngọc Vũ-Th&amp;agrave;nh ủy vi&amp;ecirc;n, Gi&amp;aacute;m đốc ĐHĐN đ&amp;atilde; ph&amp;aacute;t biểu ghi nhận v&amp;agrave; cảm ơn c&amp;aacute;c doanh nghiệp đ&amp;atilde; d&amp;agrave;nh cho SV ĐHĐN sự quan t&amp;acirc;m, hỗ trợ qu&amp;yacute; b&amp;aacute;u.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 425px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/09/02/1599060809-hinh-204.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Gi&amp;aacute;m đốc ĐHĐN trao qu&amp;agrave; hỗ trợ cho SV kh&amp;oacute; khăn&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;Trong thời gian vừa qua, ĐHĐN đ&amp;atilde; triển khai nhiều hoạt động &amp;yacute; nghĩa hỗ trợ gi&amp;uacute;p SV phải lưu lại th&amp;agrave;nh phố, thực hiện chủ trương gi&amp;atilde;n c&amp;aacute;ch x&amp;atilde; hội để ph&amp;ograve;ng, chống Covid-19, đồng thời vận động c&amp;aacute;c mạnh thường qu&amp;acirc;n, c&amp;aacute;c tấm l&amp;ograve;ng hảo t&amp;acirc;m chia sẻ, hỗ trợ SV nhu yếu phẩm phục vụ sinh hoạt.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 476px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/09/02/1599060809-hinh-205.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Đại diện nh&amp;agrave; t&amp;agrave;i trợ trao qu&amp;agrave;&amp;nbsp;cho SV kh&amp;oacute; khăn&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;Đại diện cho SV được nhận hỗ trợ, SV L&amp;ecirc; Thị Bảo Tr&amp;acirc;n (lớp 18CNACLC03, Trường ĐH Ngoại ngữ-ĐHĐN) đ&amp;atilde; b&amp;agrave;y tỏ l&amp;ograve;ng biết ơn đến c&amp;aacute;c cấp l&amp;atilde;nh đạo, doanh nh&amp;acirc;n v&amp;agrave; ĐHĐN.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 582px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/09/02/1599060810-hinh-206.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Đại diện SV ph&amp;aacute;t biểu c&amp;aacute;m ơn&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;&amp;ldquo;Sự hỗ trợ qu&amp;yacute; b&amp;aacute;u của c&amp;aacute;c doanh nghiệp thực sự c&amp;oacute; &amp;yacute; nghĩa đối với ch&amp;uacute;ng em trước thềm năm học mới. Ch&amp;uacute;ng em sẽ d&amp;agrave;nh phần kinh ph&amp;iacute; được nhận để trang trải học ph&amp;iacute; v&amp;agrave; sinh hoạt hằng ng&amp;agrave;y.&amp;rdquo;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;Trong dịp n&amp;agrave;y, C&amp;ocirc;ng ty TNHH MTV Ph&amp;aacute;t triển Nh&amp;agrave; Đa Phước v&amp;agrave; CLB Golf G8 c&amp;ograve;n hỗ trợ c&amp;aacute;c đối tượng người ngh&amp;egrave;o (quận Ngũ H&amp;agrave;nh Sơn, Cẩm Lệ&amp;hellip;) gặp kh&amp;oacute; khăn do t&amp;aacute;c động của dịch Covid-19 với tổng gi&amp;aacute; trị l&amp;agrave; 1 tỷ đồng.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: right;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: bolder; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Tin Trung t&amp;acirc;m TT-HL v&amp;agrave; Truyền th&amp;ocirc;ng ĐHĐN&lt;/span&gt;&lt;/p&gt;', '', 1, 0, 0, '[\"1\"]', 54, 99, '2021-01-29 15:33:30', NULL, NULL);
INSERT INTO `cms` VALUES (1095, 170, 'VKU: Trường Đại học vươn tầm quốc tế về nghiên cứu khoa học và hợp tác quốc tế', 'VKU: Trường Đại học vươn tầm quốc tế về nghiên cứu khoa học và hợp tác quốc tế', 'VKU: Trường Đại học vươn tầm quốc tế về nghiên cứu khoa học và hợp tác quốc tế', 'vku-truong-dai-hoc-vuon-tam-quoc-te-ve-nghien-cuu-khoa-hoc-va-hop-tac-quoc-te', '', 'VKU: Trường Đại học vươn tầm quốc tế về nghiên cứu khoa học và hợp tác quốc tế', '', '2020/09/07/h1.jpg', 'Hiện nay, trên toàn thế giới nói chung và tại Việt Nam nói riêng, một trường Đại học được xem là hiện đại, chất lượng cao phải đáp ứng đồng thời ba chức năng cơ bản: Đào tạo nguồn lực chất lượng cao, Nghiên cứu khoa học – chuyển giao công nghệ và Phục vụ xã hội. Ba hoạt động này giao thoa với nhau, có mối quan hệ hữu cơ với nhau để cùng thực hiện nhiệm vụ chiến lược của một trường Đại học. ', '', '&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: justify; text-indent: .5in;&quot;&gt;&lt;strong style=&quot;mso-bidi-font-weight: normal;&quot;&gt;&lt;em style=&quot;mso-bidi-font-style: normal;&quot;&gt;Hiện nay, tr&amp;ecirc;n to&amp;agrave;n thế giới n&amp;oacute;i chung v&amp;agrave; tại Việt Nam n&amp;oacute;i ri&amp;ecirc;ng, &lt;span class=&quot;st&quot;&gt;một trường &lt;/span&gt;&lt;/em&gt;&lt;em&gt;&lt;span style=&quot;font-style: normal; mso-bidi-font-style: italic;&quot;&gt;Đại học được xem l&amp;agrave; &lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;st&quot;&gt;&lt;em style=&quot;mso-bidi-font-style: normal;&quot;&gt;hiện đại, chất lượng cao phải đ&amp;aacute;p ứng đồng thời ba chức năng cơ bản: Đ&amp;agrave;o tạo nguồn lực chất lượng cao, &lt;/em&gt;&lt;/span&gt;&lt;em&gt;&lt;span style=&quot;font-style: normal; mso-bidi-font-style: italic;&quot;&gt;Nghi&amp;ecirc;n cứu khoa học &amp;ndash; chuyển giao c&amp;ocirc;ng nghệ&lt;/span&gt;&lt;/em&gt;&lt;em&gt;&lt;span style=&quot;font-style: normal; mso-bidi-font-style: italic;&quot;&gt;v&amp;agrave;&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;st&quot;&gt;&lt;em style=&quot;mso-bidi-font-style: normal;&quot;&gt; Phục vụ x&amp;atilde; hội. Ba hoạt động n&amp;agrave;y giao thoa với nhau, c&amp;oacute; mối quan hệ hữu cơ với nhau để c&amp;ugrave;ng thực hiện nhiệm vụ chiến lược của một trường Đại học. Trong đ&amp;oacute;, hoạt động nghi&amp;ecirc;n cứu khoa học &lt;/em&gt;&lt;/span&gt;&lt;em style=&quot;mso-bidi-font-style: normal;&quot;&gt;g&amp;oacute;p phần quan trọng để khẳng định uy t&amp;iacute;n của Nh&amp;agrave; trường, mỗi hoạt động khoa học c&amp;ocirc;ng nghệ hay một c&amp;ocirc;ng tr&amp;igrave;nh khoa học được đ&amp;aacute;nh gi&amp;aacute; cao gắn với t&amp;ecirc;n Nh&amp;agrave; trường l&amp;agrave; một lần thương hiệu v&amp;agrave; uy t&amp;iacute;n của Nh&amp;agrave; trường được n&amp;acirc;ng l&amp;ecirc;n. Đồng thời, hoạt động nghi&amp;ecirc;n cứu khoa học cũng đ&amp;oacute;ng vai tr&amp;ograve; thức đẩy n&amp;acirc;ng cao chất lượng đ&amp;agrave;o tạo, tạo ra nguồn nh&amp;acirc;n lực đ&amp;aacute;p ứng được nhu cầu ng&amp;agrave;y c&amp;agrave;ng cao của x&amp;atilde; hội. Đặc biệt, trong c&amp;aacute;c bộ ti&amp;ecirc;u ch&amp;iacute; xếp hạng c&amp;aacute;c trường đại học (quốc tế v&amp;agrave; cả trong nước), tỷ lệ phần trăm hoạt động khoa học c&amp;ocirc;ng nghệ chiếm một tỷ lệ kh&amp;aacute; lớn v&amp;agrave; quan trọng.&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; font-family: Helvetica Neue, Helvetica, Arial, sans-serif; font-size: 14px; text-align: -webkit-center;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/07/1599448618_h1.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: center;&quot; align=&quot;center&quot;&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;H&amp;igrave;nh 1. Ph&amp;ograve;ng m&amp;aacute;y hiện đại, chất lượng cao của Đại học C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng Việt-H&amp;agrave;n&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: justify; text-indent: .5in;&quot;&gt;Trường Đại học C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng Việt-H&amp;agrave;n (VKU, http://vku.udn.vn/), đơn vị th&amp;agrave;nh vi&amp;ecirc;n của Đại học Đ&amp;agrave; Nẵng, l&amp;agrave; một trong 3 Trường Đại học lớn tr&amp;ecirc;n cả nước đ&amp;agrave;o tạo chuy&amp;ecirc;n s&amp;acirc;u về lĩnh vực C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng. VKU đ&amp;atilde; v&amp;agrave; đang tập trung đ&amp;agrave;o tạo c&amp;aacute;c ng&amp;agrave;nh học mới, hiện đại v&amp;agrave; ti&amp;ecirc;n tiến nhằm đ&amp;aacute;p ứng nguồn nh&amp;acirc;n lực cho c&amp;aacute;ch mạng c&amp;ocirc;ng nghiệp lần thứ 4 v&amp;agrave; chuyển đổi số, bao gồm: (1) Kỹ thuật phần mềm; (2) Khoa học dữ liệu &amp;amp; AI; (3) IoT &amp;amp; Robotics; (4) An to&amp;agrave;n th&amp;ocirc;ng tin; (5) Thương mại điện tử; (6) Marketing số; (7) T&amp;agrave;i ch&amp;iacute;nh - Kế to&amp;aacute;n số; (8) Truyền th&amp;ocirc;ng đa phương tiện; (9) Mỹ thuật số. B&amp;ecirc;n cạnh &lt;span style=&quot;mso-bidi-font-weight: bold;&quot;&gt;một hệ thống cơ sở vật chất đồng bộ v&amp;agrave; hiện đại phục vụ cho đ&amp;agrave;o tạo được đầu tư từ nguồn kinh ph&amp;iacute; hơn 1.000 tỷ đồng của Ch&amp;iacute;nh phủ Việt Nam v&amp;agrave; 16,2 triệu USD của Ch&amp;iacute;nh phủ H&amp;agrave;n Quốc, Nh&amp;agrave; trường c&amp;ograve;n sở hữu một đội ngũ giảng vi&amp;ecirc;n nhiều kinh nghiệm giảng dạy đại học v&amp;agrave; sau đại học với 100% giảng vi&amp;ecirc;n c&amp;oacute; tr&amp;igrave;nh độ sau đại học, trong đ&amp;oacute; c&amp;oacute; 02 ph&amp;oacute; gi&amp;aacute;o sư, 34 tiến sĩ, 25 nghi&amp;ecirc;n cứu sinh. C&amp;aacute;c giảng vi&amp;ecirc;n được đ&amp;agrave;o tạo từ c&amp;aacute;c nước c&amp;oacute; nền gi&amp;aacute;o dục ti&amp;ecirc;n tiến tr&amp;ecirc;n thế giới như: Mỹ, Ph&amp;aacute;p, Đức, T&amp;acirc;y Ban Nha, Nhật Bản, Anh Quốc, H&amp;agrave;n Quốc, Newzeland, &amp;Uacute;c. Ngo&amp;agrave;i ra, h&amp;agrave;ng năm Nh&amp;agrave; trường c&amp;ograve;n tiếp nhận c&amp;aacute;c gi&amp;aacute;o sư, chuy&amp;ecirc;n gia nước ngo&amp;agrave;i về tham gia giảng dạy, nghi&amp;ecirc;n cứu khoa học v&amp;agrave; hướng dẫn sinh vi&amp;ecirc;n thực hiện c&amp;aacute;c dự &amp;aacute;n khởi nghiệp.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span style=&quot;mso-bidi-font-weight: bold;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0in 0in 6pt; text-indent: 0.5in;&quot; align=&quot;center&quot;&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; font-family: Helvetica Neue, Helvetica, Arial, sans-serif; font-size: 14px; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/07/1599448642_h2.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: center; text-indent: .5in;&quot; align=&quot;center&quot;&gt;&lt;span style=&quot;mso-bidi-font-weight: bold;&quot;&gt;H&amp;igrave;nh 2. C&amp;aacute;c ng&amp;agrave;nh đ&amp;agrave;o tạo hiện đại của Trường Đại học C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng Việt -H&amp;agrave;n&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span style=&quot;mso-bidi-font-weight: bold;&quot;&gt;Tất cả c&amp;aacute;c yếu tố tr&amp;ecirc;n đ&amp;atilde; h&amp;igrave;nh th&amp;agrave;nh một nền tảng cơ bản, vững chắc để VKU x&amp;acirc;y dựng, ph&amp;aacute;t triển v&amp;agrave; tạo dựng một thương hiệu uy t&amp;iacute;n, chất lượng trong c&amp;aacute;c hoạt động nghi&amp;ecirc;n cứu khoa học. &lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span style=&quot;,serif; mso-ansi-language: EN-US;&quot;&gt;Năm 2020 được Nh&amp;agrave; trường xem l&amp;agrave; năm bản lề v&amp;agrave; đ&amp;oacute;ng vai tr&amp;ograve; rất quan trọng trong chiến lược ph&amp;aacute;t triển v&amp;agrave; n&amp;acirc;ng tầm học hiệu VKU, h&amp;ograve;a chung v&amp;agrave;o mạch chảy của cuộc c&amp;aacute;ch mạng c&amp;ocirc;ng nghiệp lần thứ 4 v&amp;agrave; chuyển đổi số. Một trong số c&amp;aacute;c hoạt động then chốt của Nh&amp;agrave; trường đ&amp;oacute; l&amp;agrave; việc tổ chức chuỗi c&amp;aacute;c sự kiện khoa học v&amp;agrave; c&amp;ocirc;ng nghệ v&amp;agrave;o những th&amp;aacute;ng cuối năm 2020:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span lang=&quot;FR-BE&quot;&gt;+ Ng&amp;agrave;y 27/11/2020: Tổ chức Hội thảo khoa học quốc gia CITA 2020 lần thứ 9;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span lang=&quot;FR-BE&quot;&gt;+ Ng&amp;agrave;y 28/11/2020: Tổ chức Seminar khoa học d&amp;agrave;nh cho c&amp;aacute;c nh&amp;agrave; nghi&amp;ecirc;n cứu trẻ do GS.TSKH Nguyễn Ngọc Th&amp;agrave;nh v&amp;agrave; c&amp;aacute;c Gi&amp;aacute;o sư từ Trường Đại học B&amp;aacute;ch Khoa Wroclaw, Ba Lan chủ tr&amp;igrave;.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span lang=&quot;FR-BE&quot;&gt;+ Từ ng&amp;agrave;y 30/11/2020 đến ng&amp;agrave;y 03/12/2020: Đồng tổ chức Hội thảo khoa học quốc tế ICCCI 2020 lần 12, phối hợp với Trường Đại học B&amp;aacute;ch khoa Wroclaw, Ba Lan.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span style=&quot;,serif; color: black;&quot; lang=&quot;FR-BE&quot;&gt;Hội thảo quốc tế ICCCI (&lt;/span&gt;&lt;span style=&quot;,serif; mso-fareast-language: EN-GB; mso-bidi-font-style: italic;&quot; lang=&quot;FR-BE&quot;&gt;International Conference on Computational Collective Intelligence&lt;/span&gt;&lt;span style=&quot;,serif; color: black;&quot; lang=&quot;FR-BE&quot;&gt;) l&amp;agrave; một hội thảo khoa học quốc tế d&amp;agrave;nh cho nghi&amp;ecirc;n cứu trong lĩnh vực tr&amp;iacute; tuệ nh&amp;oacute;m v&amp;agrave; t&amp;iacute;nh to&amp;aacute;n th&amp;ocirc;ng minh. Đ&amp;acirc;y l&amp;agrave; diễn đ&amp;agrave;n chia sẻ kinh nghiệm v&amp;agrave; trao đổi nghi&amp;ecirc;n cứu khoa học về tr&amp;iacute; tuệ nh&amp;oacute;m, c&amp;aacute;c phương ph&amp;aacute;p t&amp;iacute;nh to&amp;aacute;n th&amp;ocirc;ng minh dựa tr&amp;ecirc;n m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; c&amp;aacute;c ứng dụng trong c&amp;aacute;c lĩnh vực:&lt;span style=&quot;mso-spacerun: yes;&quot;&gt;&amp;nbsp; &lt;/span&gt;xử l&amp;yacute; dữ liệu v&amp;agrave; tri thức từ c&amp;aacute;c nguồn độc lập, đặc biệt l&amp;agrave; xử l&amp;yacute; tập thể, c&amp;ocirc;ng nghệ tri thức v&amp;agrave; web ngữ nghĩa, ra quyết định nh&amp;oacute;m, hệ thống đa t&amp;aacute;c tử, điện to&amp;aacute;n đồng thuận, hệ thống đề xuất v&amp;agrave; ph&amp;acirc;n t&amp;iacute;ch mạng x&amp;atilde; hội. Kỷ yếu hội thảo sẽ được xuất bản trong series Lecture Notes of Artificial Intelligence (LNAI) về tr&amp;iacute; tuệ nh&amp;acirc;n tạo của Nh&amp;agrave; xuất bản Springer. Qua 11 lần tổ chức tại c&amp;aacute;c quốc gia Ba Lan, Đ&amp;agrave;i Loan, Việt Nam, Rumani, H&amp;agrave;n Quốc, T&amp;acirc;y Ban Nha, Hy Lạp, S&amp;iacute;p, Anh Quốc v&amp;agrave; Ph&amp;aacute;p, hội thảo ICCCI đ&amp;atilde; trở th&amp;agrave;nh một diễn đ&amp;agrave;n quốc tế cấp cao thường ni&amp;ecirc;n d&amp;agrave;nh cho c&amp;aacute;c nh&amp;agrave; nghi&amp;ecirc;n cứu, giảng dạy v&amp;agrave; quản l&amp;yacute; gặp gỡ, trao đổi học thuật, giới thiệu c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh nghi&amp;ecirc;n cứu khoa học hữu &amp;iacute;ch, những &amp;yacute; tưởng độc đ&amp;aacute;o v&amp;agrave; c&amp;aacute;c ứng dụng nổi bật trong lĩnh vực c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin n&amp;oacute;i chung v&amp;agrave; tr&amp;iacute; tuệ nh&amp;oacute;m, t&amp;iacute;nh to&amp;aacute;n th&amp;ocirc;ng minh n&amp;oacute;i ri&amp;ecirc;ng. &lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: center;&quot; align=&quot;center&quot;&gt;&lt;span style=&quot;,serif; color: black;&quot; lang=&quot;FR-BE&quot;&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; font-family: Helvetica Neue, Helvetica, Arial, sans-serif; font-size: 14px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/07/1599448654_h3.jpg&quot; alt=&quot;&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: center; text-indent: .5in;&quot; align=&quot;center&quot;&gt;&lt;span style=&quot;,serif; color: black;&quot; lang=&quot;FR-BE&quot;&gt;H&amp;igrave;nh 3. Hội thảo khoa học quốc tế ICCCI 2020 lần thứ 12&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span style=&quot;,serif; color: black;&quot; lang=&quot;FR-BE&quot;&gt;Hội thảo quốc tế ICCCI được s&amp;aacute;ng lập, chủ tr&amp;igrave; bởi GS.TSKH Nguyễn Ngọc Th&amp;agrave;nh (Đại học B&amp;aacute;ch Khoa Wrocław, Ba Lan). GS.TSKH Nguyễn Ngọc Th&amp;agrave;nh được phong h&amp;agrave;m gi&amp;aacute;o sư của cả 2 quốc gia Việt Nam v&amp;agrave; Ba Lan. Hiện nay, &amp;ocirc;ng c&amp;ograve;n l&amp;agrave; th&amp;agrave;nh vi&amp;ecirc;n Hội đồng thẩm định chất lượng khoa học nhiệm kỳ đầu ti&amp;ecirc;n của nước Cộng h&amp;ograve;a Ba Lan (l&amp;agrave; người nước ngo&amp;agrave;i duy nhất tại Ba Lan). Hội thảo c&amp;ograve;n được sự hỗ trợ chuy&amp;ecirc;n m&amp;ocirc;n của Ủy ban kỹ thuật của IEEE SMC về tr&amp;iacute; tuệ nh&amp;oacute;m v&amp;agrave; đội ngũ chuy&amp;ecirc;n gia c&amp;oacute; uy t&amp;iacute;n đến từ Ba Lan, Đ&amp;agrave;i Loan, Rumani, H&amp;agrave;n Quốc, T&amp;acirc;y Ban Nha, Hy Lạp, S&amp;iacute;p, Đức, Anh Quốc v&amp;agrave; Ph&amp;aacute;p. &lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span lang=&quot;FR-BE&quot;&gt;CITA (Conference on Information Technology and its Applications) l&amp;agrave; chuỗi hội thảo khoa học quốc gia về c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin v&amp;agrave; ứng dụng trong c&amp;aacute;c lĩnh vực, được tổ chức h&amp;agrave;ng năm tại th&amp;agrave;nh phố Đ&amp;agrave; Nẵng, Việt Nam. Chuỗi Hội thảo được s&amp;aacute;ng lập v&amp;agrave; tổ chức bởi Trường Đại học C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng Việt H&amp;agrave;n (VKU), Đại học Đ&amp;agrave; Nẵng. Mục ti&amp;ecirc;u ch&amp;iacute;nh của Hội thảo nhằm tạo ra một diễn đ&amp;agrave;n quy tụ v&amp;agrave; kết nối c&amp;aacute;c nh&amp;agrave; nghi&amp;ecirc;n cứu, c&amp;aacute;c nh&amp;agrave; khoa học v&amp;agrave; c&amp;aacute;c chuy&amp;ecirc;n gia trong nước v&amp;agrave; quốc tế tham gia c&amp;ocirc;ng bố, thảo luận v&amp;agrave; chia sẻ về c&amp;aacute;c vấn đề mới trong c&amp;aacute;c lĩnh vực c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin v&amp;agrave; ứng dụng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: center;&quot; align=&quot;center&quot;&gt;&lt;span lang=&quot;FR-BE&quot;&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; font-family: Helvetica Neue, Helvetica, Arial, sans-serif; font-size: 14px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/07/1599448661_h4.jpg&quot; alt=&quot;&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: center; text-indent: .5in;&quot; align=&quot;center&quot;&gt;&lt;span lang=&quot;FR-BE&quot;&gt;H&amp;igrave;nh 4. Hội thảo khoa học quốc gia CITA 2020 lần thứ 9&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 6.0pt; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span style=&quot;,serif; mso-ansi-language: EN-US;&quot;&gt;CITA l&amp;agrave; hội thảo MIỄN PH&amp;Iacute; ho&amp;agrave;n to&amp;agrave;n ph&amp;iacute; đăng b&amp;agrave;i v&amp;agrave; ph&amp;iacute; tham dự hội nghị. Tất cả c&amp;aacute;c b&amp;agrave;i b&amp;aacute;o chất lượng được chấp nhận b&amp;aacute;o c&amp;aacute;o tại hội thảo sẽ được xuất bản trong Kỷ yếu hội thảo CITA (xuất bản trước khi diễn ra Hội thảo, c&amp;oacute; m&amp;atilde; số ISBN). Hội thảo sẽ bao gồm 2 phi&amp;ecirc;n Tiếng Việt v&amp;agrave; Tiếng Anh. Phi&amp;ecirc;n Tiếng Anh d&amp;agrave;nh cho c&amp;aacute;c b&amp;agrave;i b&amp;aacute;o &lt;/span&gt;&lt;span lang=&quot;FR-BE&quot;&gt;viết bằng tiếng Anh v&amp;agrave; tr&amp;igrave;nh b&amp;agrave;y b&amp;aacute;o c&amp;aacute;o bằng tiếng Anh tại Hội thảo. Ngo&amp;agrave;i ra, phi&amp;ecirc;n bản mở rộng của c&amp;aacute;c b&amp;agrave;i b&amp;aacute;o viết bằng Tiếng Anh c&amp;oacute; chất lượng cao sẽ c&amp;oacute; cơ hội được mời nộp cho một tạp ch&amp;iacute; quốc tế uy t&amp;iacute;n sau khi được tổ chức phản biện lại.&lt;span style=&quot;mso-spacerun: yes;&quot;&gt;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: justify; text-indent: .5in;&quot;&gt;Hội thảo CITA năm 2020 (&lt;a href=&quot;http://vku.udn.vn/cita2020&quot;&gt;http://vku.udn.vn/cita2020&lt;/a&gt;), l&amp;agrave; hội thảo lần thứ 9, được tổ chức dưới sự bảo trợ chuy&amp;ecirc;n m&amp;ocirc;n của c&amp;aacute;c nh&amp;agrave; khoa học uy t&amp;iacute;n quốc tế v&amp;agrave; trong nước thuộc lĩnh vực C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin như:&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: justify; text-indent: .5in;&quot;&gt;&lt;span style=&quot;mso-fareast-font-family: Tahoma; mso-font-kerning: .5pt; mso-fareast-language: ZH-CN; mso-bidi-language: HI;&quot;&gt;+ &lt;strong&gt;GS. TS. Marcin Paprzycki&lt;/strong&gt;&amp;nbsp;- Senior Member of the ACM; Senior Member of the IEEE&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: justify; text-indent: .5in;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;mso-fareast-font-family: Tahoma; mso-font-kerning: .5pt; mso-fareast-language: ZH-CN; mso-bidi-language: HI;&quot;&gt;+ PGS.TS. Vijender Kumar Solanki&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;mso-fareast-font-family: Tahoma; mso-font-kerning: .5pt; mso-fareast-language: ZH-CN; mso-bidi-language: HI;&quot;&gt;&amp;nbsp;- CSE CMR Institute of Technology Hyderabad, TS, India&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: justify; text-indent: .5in;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;mso-fareast-font-family: Tahoma; mso-font-kerning: .5pt; mso-fareast-language: ZH-CN; mso-bidi-language: HI;&quot;&gt;+ GS.TSKH. Ngoc-Thanh Nguyen &lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;mso-fareast-font-family: Tahoma; mso-font-kerning: .5pt; mso-fareast-language: ZH-CN; mso-bidi-language: HI;&quot;&gt;- IEEE SMC Technical Committee on Computational Collective Intelligence; Wroclaw University of Science and Technology, Poland&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 0in; margin-right: 0in; margin-bottom: 6.0pt; margin-left: 0in; text-align: justify; text-indent: .5in;&quot;&gt;&lt;strong style=&quot;mso-bidi-font-weight: normal;&quot;&gt;&lt;span style=&quot;mso-fareast-font-family: Tahoma; mso-font-kerning: .5pt; mso-fareast-language: ZH-CN; mso-bidi-language: HI;&quot;&gt;+ GS.TS Nguyễn Thanh Thuỷ&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;mso-fareast-font-family: Tahoma; mso-font-kerning: .5pt; mso-fareast-language: ZH-CN; mso-bidi-language: HI;&quot;&gt;&amp;nbsp;- Trường Đại học C&amp;ocirc;ng nghệ, Đại học Quốc gia H&amp;agrave; Nội.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoBodyText&quot; style=&quot;margin-bottom: 6.0pt; text-align: justify; text-indent: .5in; line-height: normal;&quot;&gt;&lt;strong style=&quot;mso-bidi-font-weight: normal;&quot;&gt;&lt;em style=&quot;mso-bidi-font-style: normal;&quot;&gt;&lt;span style=&quot;,serif; mso-ansi-language: EN-US;&quot;&gt;C&amp;oacute; thể n&amp;oacute;i rằng, với chuỗi c&amp;aacute;c sự kiện khoa học c&amp;ocirc;ng nghệ n&amp;oacute;i tr&amp;ecirc;n, c&amp;ugrave;ng với rất nhiều c&amp;aacute;c hoạt động, dự &amp;aacute;n, đề t&amp;agrave;i nghi&amp;ecirc;n cứu khoa học v&amp;agrave; chuyển giao c&amp;ocirc;ng nghệ kh&amp;aacute;c m&amp;agrave; VKU đ&amp;atilde; v&amp;agrave; đang thực hiện một c&amp;aacute;ch c&amp;oacute; hiệu quả, VKU từng bước ph&amp;aacute;t triển như một học hiệu vươn tầm quốc tế, đảm bảo v&amp;agrave; đ&amp;aacute;p ứng một c&amp;aacute;ch tốt nhất nhu cầu đ&amp;agrave;o tạo nguồn lực chất lượng cao kh&amp;ocirc;ng chỉ ở trong nước m&amp;agrave; cả thị trường quốc tế.&lt;/span&gt;&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoBodyText&quot; style=&quot;margin-bottom: 6.0pt; text-align: justify; text-indent: .5in; line-height: normal;&quot;&gt;&lt;strong style=&quot;mso-bidi-font-weight: normal;&quot;&gt;&lt;span style=&quot;,serif; mso-ansi-language: EN-US;&quot;&gt;(Xem th&amp;ecirc;m b&amp;agrave;i b&amp;aacute;o gốc tại: https://dsa.org.vn/dai-hoc-cong-nghe-thong-tin-truyen-thong-viet-han-voi-muc-tieu-vuon-tam-quoc-te-trong-nghien-cuu-khoa-hoc-hop-tac-quoc-te/)&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;', '', 1, 1, 0, '[\"1\"]', 674, 99, '2021-01-29 15:33:30', NULL, NULL);
INSERT INTO `cms` VALUES (1096, 123, 'Thông báo số 1 về việc nhập học đối với thí sinh trúng tuyển đại học năm 2020', 'Thông báo số 1 về việc nhập học đối với thí sinh trúng tuyển đại học năm 2020', 'Thông báo số 1 về việc nhập học đối với thí sinh trúng tuyển đại học năm 2020', 'thong-bao-so-1-ve-viec-nhap-hoc-doi-voi-thi-sinh-trung-tuyen-dai-hoc-nam-2020', '', 'Thông báo số 1 về việc nhập học đối với thí sinh trúng tuyển đại học năm 2020', '', '', 'Trường Đại học Công nghệ Thông tin và Truyền thông Việt Hàn - Đại học Đà Nẵng thông báo hướng dẫn về thủ tục nhập học (Đợt 1) đối với thí sinh trúng tuyển theo phương thức xét tuyển thẳng, xét tuyển theo phương thức tuyển sinh riêng, xét tuyển theo kết quả học tập THPT (xét học bạ).', '', '&lt;p&gt;Trường Đại học C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng Việt H&amp;agrave;n - Đại học Đ&amp;agrave; Nẵng th&amp;ocirc;ng b&amp;aacute;o hướng dẫn về thủ tục nhập học (Đợt 1) đối với th&amp;iacute; sinh tr&amp;uacute;ng tuyển theo phương thức x&amp;eacute;t tuyển thẳng, x&amp;eacute;t tuyển theo phương thức tuyển sinh ri&amp;ecirc;ng, x&amp;eacute;t tuyển theo kết quả học tập THPT (x&amp;eacute;t học bạ) &lt;strong&gt;đợt 1&lt;/strong&gt;:&lt;/p&gt;\r\n&lt;h3&gt;I. CHUẨN BỊ HỒ SƠ NHẬP HỌC&lt;/h3&gt;\r\n&lt;p&gt;Mỗi th&amp;iacute; sinh chuẩn bị bộ hồ sơ gồm c&amp;aacute;c loại giấy tờ sau đ&amp;acirc;y:&lt;/p&gt;\r\n&lt;p&gt;1. Giấy b&amp;aacute;o tr&amp;uacute;ng tuyển (nhận tại Ph&amp;ograve;ng Đ&amp;agrave;o tạo nếu nhập học trực tiếp); đối với th&amp;iacute; sinh x&amp;eacute;t tuyển theo h&amp;igrave;nh THPT c&amp;oacute; thể nhận giấy b&amp;aacute;o tr&amp;uacute;ng tuyển ngay sau khi c&amp;oacute; kết quả THPT;&lt;/p&gt;\r\n&lt;p&gt;2. Bản sao c&amp;ocirc;ng chứng Bằng tốt nghiệp THPT hay BTVHTH. Th&amp;iacute; sinh tốt nghiệp THPT năm 2020 m&amp;agrave; chưa c&amp;oacute; Bằng tốt nghiệp THPT th&amp;igrave; nộp bản ch&amp;iacute;nh Giấy chứng nhận tốt nghiệp THPT tạm thời;&lt;/p&gt;\r\n&lt;p&gt;3. Bản sao c&amp;ocirc;ng chứng Học bạ THPT;&lt;/p&gt;\r\n&lt;p&gt;4. Bản ch&amp;iacute;nh Giấy chứng nhận kết quả thi THPT năm 2020 (đối với th&amp;iacute; sinh tốt nghiệp THPT năm 2020). Nếu th&amp;iacute; sinh đ&amp;atilde; nộp khi x&amp;aacute;c nhận nhập học th&amp;igrave; nhận bản photo khi l&amp;agrave;m thủ tục nhập học tại Ph&amp;ograve;ng Đ&amp;agrave;o tạo.&lt;/p&gt;\r\n&lt;p&gt;5. Bản sao Giấy khai sinh; bản sao Hộ khẩu; 03 bản photo CMND; 02 ảnh thẻ cỡ 3x4 chụp trong v&amp;ograve;ng 6 th&amp;aacute;ng;&lt;/p&gt;\r\n&lt;p&gt;6. Bản sao c&amp;ocirc;ng chứng c&amp;aacute;c giấy tờ li&amp;ecirc;n quan đến đối tượng x&amp;eacute;t tuyển thẳng;&lt;/p&gt;\r\n&lt;p&gt;7. Bản sao c&amp;ocirc;ng chứng c&amp;aacute;c giấy tờ ưu ti&amp;ecirc;n kh&amp;aacute;c (Nếu c&amp;oacute;).&lt;/p&gt;\r\n&lt;p&gt;8. Đối với trường hợp l&amp;agrave; Nam sinh vi&amp;ecirc;n th&amp;igrave; bắt buộc nộp Giấy di chuyển qu&amp;acirc;n sự, đăng k&amp;yacute; vắng mặt qu&amp;acirc;n sự v&amp;agrave; đăng k&amp;yacute; nghĩa vụ qu&amp;acirc;n sự&lt;/p&gt;\r\n&lt;p&gt;9. Hồ sơ v&amp;agrave; giấy chuyển sinh hoạt Đảng/Đo&amp;agrave;n&lt;/p&gt;\r\n&lt;h3&gt;II. C&amp;Aacute;C BƯỚC THỰC HIỆN NHẬP HỌC&lt;/h3&gt;\r\n&lt;p&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;&lt;strong&gt;1. NHẬP HỌC TRỰC TUYẾN&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;BƯỚC 1: X&amp;Aacute;C NHẬN NHẬP HỌC&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Th&amp;iacute; sinh đ&amp;atilde; thực hiện từ ng&amp;agrave;y 24/8/2020 &amp;ndash; đến hết 17h00 ng&amp;agrave;y 11/09/2020&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;BƯỚC 2: NHẬP TH&amp;Ocirc;NG TIN V&amp;Agrave; NỘP HỒ SƠ TRỰC TUYẾN&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Từ ng&amp;agrave;y 8h00 09/09 đến trước 17 giờ 00 ng&amp;agrave;y 11/9/2020 Th&amp;iacute; sinh đăng nhập trang tin điện tử &lt;a href=&quot;http://vku.udn.vn/nhaphoc2020/&quot;&gt;http://vku.udn.vn/nhaphoc2020/&lt;/a&gt; để xem Hướng dẫn chi tiết về điền th&amp;ocirc;ng tin sinh vi&amp;ecirc;n v&amp;agrave; nộp hồ sơ.&lt;/p&gt;\r\n&lt;p&gt;Hồ sơ cần nộp trực tuyến l&amp;agrave;:&lt;/p&gt;\r\n&lt;p&gt;- Bản sao giấy khai sinh&lt;/p&gt;\r\n&lt;p&gt;- Bản sao hộ khẩu (Chụp h&amp;igrave;nh 04 trang gồm trang b&amp;igrave;a v&amp;agrave; c&amp;aacute;c trang c&amp;oacute; th&amp;ocirc;ng tin ba, mẹ, v&amp;agrave; bản th&amp;acirc;n)&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;BƯỚC 3: NỘP TIỀN&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Từ ng&amp;agrave;y 09/09 đến trước 17 giờ 00 ng&amp;agrave;y 11/9/2020&lt;/p&gt;\r\n&lt;p&gt;Tổng số tiền nhập học tạm nộp:&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;1. Ng&amp;agrave;nh C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; C&amp;ocirc;ng nghệ kỹ thuật m&amp;aacute;y t&amp;iacute;nh:&lt;/strong&gt; 6.416.805 đồng, gồm c&amp;aacute;c khoản:&lt;/p&gt;\r\n&lt;p&gt;a. Học ph&amp;iacute; học kỳ 1 năm học 2020-2021&lt;/p&gt;\r\n&lt;p&gt;b. Bảo hiểm y tế (3 th&amp;aacute;ng 10,11,12 năm 2020)&lt;/p&gt;\r\n&lt;p&gt;c. Lệ ph&amp;iacute; kh&amp;aacute;m sức khỏe&lt;/p&gt;\r\n&lt;p&gt;d. Kiểm tra tiếng Anh đầu v&amp;agrave;o&lt;/p&gt;\r\n&lt;p&gt;e. Bảo hiểm th&amp;acirc;n thể (tự nguyện)&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;2. Ng&amp;agrave;nh Quản trị kinh doanh:&lt;/strong&gt; 5.930.805 đồng, gồm c&amp;aacute;c khoản:&lt;/p&gt;\r\n&lt;p&gt;a. Học ph&amp;iacute; học kỳ 1 năm học 2020-2021&lt;/p&gt;\r\n&lt;p&gt;b. Bảo hiểm y tế (3 th&amp;aacute;ng 10,11,12 năm 2020)&lt;/p&gt;\r\n&lt;p&gt;c. Lệ ph&amp;iacute; kh&amp;aacute;m sức khỏe&lt;/p&gt;\r\n&lt;p&gt;d. Kiểm tra tiếng Anh đầu v&amp;agrave;o&lt;/p&gt;\r\n&lt;p&gt;e. Bảo hiểm th&amp;acirc;n thể (tự nguyện)&lt;/p&gt;\r\n&lt;p&gt;3. Tổng số tiền nhập học tạm nộp chuyển về:&lt;/p&gt;\r\n&lt;p&gt;T&amp;ecirc;n T&amp;agrave;i khoản: &lt;strong&gt;TRƯỜNG ĐẠI HỌC CNTT V&amp;Agrave; TRUYỀN TH&amp;Ocirc;NG VIỆT &amp;ndash; H&amp;Agrave;N&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Số t&amp;agrave;i khoản: &lt;strong&gt;2015201003532&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;T&amp;ecirc;n ng&amp;acirc;n h&amp;agrave;ng: Ng&amp;acirc;n h&amp;agrave;ng N&amp;ocirc;ng nghiệp v&amp;agrave; Ph&amp;aacute;t triển N&amp;ocirc;ng th&amp;ocirc;n Việt Nam &amp;ndash; Chi nh&amp;aacute;nh T&amp;acirc;n Ch&amp;iacute;nh &amp;ndash; Nam Đ&amp;agrave; Nẵng&lt;/p&gt;\r\n&lt;p&gt;4. Nội dung nộp tiền hoặc chuyển tiền theo c&amp;uacute; ph&amp;aacute;p sau: &lt;strong&gt;AAAA,XXXX,YYYY&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Trong đ&amp;oacute;:&lt;/p&gt;\r\n&lt;p&gt;- AAAA: Số Chứng minh thư hoặc số căn cước c&amp;ocirc;ng d&amp;acirc;n&lt;/p&gt;\r\n&lt;p&gt;- XXXX: Họ v&amp;agrave; t&amp;ecirc;n&lt;/p&gt;\r\n&lt;p&gt;- YYYY: Nhập học 2020&lt;/p&gt;\r\n&lt;p&gt;V&amp;iacute; dụ: 0123456789, Nguyễn Văn A, Nhập học 2020&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;BƯỚC 4: Đăng k&amp;yacute; nội tr&amp;uacute; K&amp;yacute; t&amp;uacute;c x&amp;aacute; (nếu th&amp;iacute; sinh c&amp;oacute; nhu cầu)&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;&lt;strong&gt;2. NHẬP HỌC TRỰC TIẾP&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;Khi tập trung đến Trường theo th&amp;ocirc;ng b&amp;aacute;o của Nh&amp;agrave; trường (Dự kiến ng&amp;agrave;y 15-16/09/2020), th&amp;iacute; sinh cần nộp bổ sung hồ sơ đầy đủ gồm c&amp;aacute;c loại giấy tờ được quy định tại mục I.&lt;/p&gt;\r\n&lt;h3&gt;III. MỘT SỐ LƯU &amp;Yacute;&lt;/h3&gt;\r\n&lt;p&gt;1. Số điện thoại hỗ trợ nhập học: 0236 3667 129, 0236 2678 234.&lt;/p&gt;\r\n&lt;p&gt;Group hỗ trợ hỏi đ&amp;aacute;p d&amp;agrave;nh cho T&amp;acirc;n sinh vi&amp;ecirc;n:&amp;nbsp;&lt;a style=&quot;font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;&quot; href=&quot;https://www.facebook.com/groups/k20.vku.sict/&quot;&gt;https://www.facebook.com/groups/k20.vku.sict/&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;Fanpage hỗ trợ nhập học:&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;2. Th&amp;iacute; sinh dự thi Tốt nghiệp THPT đợt 2 năm 2020 được bảo lưu kết quả x&amp;eacute;t tuyển v&amp;agrave; thực hiện c&amp;aacute;c thủ tục nhập học ngay sau khi c&amp;oacute; kết quả tốt nghiệp THPT 2020&lt;/p&gt;', '', 1, 1, 0, '[\"1\"]', 1702, 99, '2021-01-29 15:33:30', NULL, NULL);
INSERT INTO `cms` VALUES (1097, 123, 'Công bố trúng tuyển theo phương thức tuyển sinh riêng bổ sung 2020', 'Công bố trúng tuyển theo phương thức tuyển sinh riêng bổ sung 2020', 'Công bố trúng tuyển theo phương thức tuyển sinh riêng bổ sung 2020', 'cong-bo-trung-tuyen-theo-phuong-thuc-tuyen-sinh-rieng-bo-sung-2020', '', 'Công bố trúng tuyển theo phương thức tuyển sinh riêng bổ sung 2020', '', '2020/09/08/congbo.jpg', 'Đã có danh sách các thí sinh trúng tuyển (có điều kiện) theo hình thức xét tuyển riêng 2020 đợt 1 bổ sung của Trường Đại học Công nghệ Thông tin &amp; Truyền thông Việt Hàn - ĐHĐN\r\n', '', '&lt;p&gt;&lt;span style=&quot;font-size: 1.17em;&quot;&gt;Đ&amp;atilde; c&amp;oacute; danh s&amp;aacute;ch c&amp;aacute;c th&amp;iacute; sinh tr&amp;uacute;ng tuyển (c&amp;oacute; điều kiện) theo h&amp;igrave;nh thức x&amp;eacute;t tuyển ri&amp;ecirc;ng 2020 đợt 1 bổ sung của Trường Đại học C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin &amp;amp; Truyền th&amp;ocirc;ng Việt H&amp;agrave;n - ĐHĐN&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;Chi tiết danh s&amp;aacute;ch xem tại: &lt;a href=&quot;http://tuyensinh.vku.udn.vn/tsr_vku_bosung.pdf&quot;&gt;http://tuyensinh.vku.udn.vn/tsr_vku_bosung.pdf&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; font-family: Helvetica Neue, Helvetica, Arial, sans-serif; font-size: 14px; text-align: -webkit-center;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/08/1599556930_New%20Project%20(6).jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;h3&gt;THỦ TỤC X&amp;Aacute;C NHẬN NHẬP HỌC&lt;/h3&gt;\r\n&lt;p&gt;- Th&amp;iacute; sinh phải x&amp;aacute;c nhận nhập học trực tuyến tại địa chỉ &lt;a href=&quot;http://vku.udn.vn/nhaphoc&quot;&gt;http://vku.udn.vn/nhaphoc&lt;/a&gt; từ ng&amp;agrave;y 8h00 09/9 đến 17h00 ng&amp;agrave;y 12/9/2020.&lt;/p&gt;\r\n&lt;p&gt;- Th&amp;iacute; sinh tiếp tục x&amp;aacute;c nhận nhập học bằng c&amp;aacute;ch:&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;+ Đối tượng tốt nghiệp trước 2020: Nộp bản ch&amp;iacute;nh học bạ v&amp;agrave; photo bằng tốt nghiệp.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;+ Đối tượng thi THPT 2020 đợt 1: nộp bản ch&amp;iacute;nh phiếu điểm kết quả thi THPT 2020 v&amp;agrave; bản ch&amp;iacute;nh học bạ.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;+ Đối tượng thi THPT 2020 đợt 2: nộp phiếu đăng k&amp;yacute; nhập học v&amp;agrave;o Đại học Đ&amp;agrave; Nẵng năm 2020 (tải mẫu tại &lt;a href=&quot;http://tuyensinh.sict.udn.vn/phieu_nhap_hoc.pdf&quot;&gt;http://tuyensinh.sict.udn.vn/phieu_nhap_hoc.pdf&lt;/a&gt;) v&amp;agrave; bản ch&amp;iacute;nh học bạ.&lt;/p&gt;\r\n&lt;p&gt;- Hồ sơ được gửi về trường theo th&amp;ocirc;ng tin sau:&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Thời gian:&lt;/strong&gt; &lt;em&gt;&lt;strong&gt;Trước 17 giờ 00 ng&amp;agrave;y 12/9/2020 (t&amp;iacute;nh theo dấu bưu điện)&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Địa chỉ:&lt;/strong&gt; Trường ĐH C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng Việt - H&amp;agrave;n, Đại học Đ&amp;agrave; Nẵng, 470 Trần Đại Nghĩa, phường Hòa Quý, qu&amp;acirc;̣n Ngũ H&amp;agrave;nh Sơn, Tp Đ&amp;agrave; Nẵng.&lt;/p&gt;\r\n&lt;p&gt;KẾ HOẠCH NHẬP HỌC cho đối tượng n&amp;agrave;y sẽ được th&amp;ocirc;ng b&amp;aacute;o sau tr&amp;ecirc;n website.&lt;/p&gt;', '', 1, 1, 0, '[\"1\"]', 863, 99, '2021-01-29 15:33:30', NULL, NULL);
INSERT INTO `cms` VALUES (1098, 123, 'Công bố trúng tuyển học bạ bổ sung đợt 1 năm 2020 và thông báo số 2 việc việc nhập học', 'Công bố trúng tuyển học bạ bổ sung đợt 1 năm 2020 và thông báo số 2 việc việc nhập học', 'Công bố trúng tuyển học bạ bổ sung đợt 1 năm 2020 và thông báo số 2 việc việc nhập học', 'cong-bo-trung-tuyen-hoc-ba-bo-sung-dot-1-nam-2020-va-thong-bao-so-2-ve-viec-nhap-hoc', '', 'Công bố trúng tuyển học bạ bổ sung đợt 1 năm 2020 và thông báo số 2 về việc nhập học', '', '2020/09/09/450c3f48177ce822b16d.jpg', 'Đã có danh sách các thí sinh trúng tuyển (có điều kiện) theo hình thức xét tuyển riêng 2020 đợt 1 bổ sung của Trường Đại học Công nghệ Thông tin &amp; Truyền thông Việt Hàn - ĐHĐN. Thí sinh tra cứu bằng cách nhập CMND/Căn cước công dân tại: http://ts.udn.vn/ketqua', '', '&lt;p&gt;Đ&amp;atilde; c&amp;oacute; danh s&amp;aacute;ch c&amp;aacute;c th&amp;iacute; sinh tr&amp;uacute;ng tuyển (c&amp;oacute; điều kiện) theo h&amp;igrave;nh thức x&amp;eacute;t tuyển ri&amp;ecirc;ng 2020 đợt 1 bổ sung của Trường Đại học C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin &amp;amp; Truyền th&amp;ocirc;ng Việt H&amp;agrave;n - ĐHĐN. Th&amp;iacute; sinh tra cứu bằng c&amp;aacute;ch nhập CMND/Căn cước c&amp;ocirc;ng d&amp;acirc;n tại: &lt;a href=&quot;http://ts.udn.vn/ketqua&quot;&gt;http://ts.udn.vn/ketqua&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; font-family: Helvetica Neue, Helvetica, Arial, sans-serif; font-size: 14px; text-align: -webkit-center; margin-left: auto; margin-right: auto;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/09/1599642875_tra%20cuu.jpg&quot; alt=&quot;&quot; width=&quot;70%&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; font-family: Helvetica Neue, Helvetica, Arial, sans-serif; font-size: 14px; text-align: -webkit-center;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/09/1599641343_450c3f48177ce822b16d.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;Trường Đại học C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng Việt H&amp;agrave;n - Đại học Đ&amp;agrave; Nẵng th&amp;ocirc;ng b&amp;aacute;o hướng dẫn về thủ tục nhập học (Đợt 2) đối với th&amp;iacute; sinh tr&amp;uacute;ng tuyển theo x&amp;eacute;t tuyển theo kết quả học tập THPT (x&amp;eacute;t học bạ)&amp;nbsp;&lt;strong&gt;đợt 1 bổ sung&lt;/strong&gt;:&lt;/p&gt;\r\n&lt;h3&gt;I. CHUẨN BỊ HỒ SƠ NHẬP HỌC&lt;/h3&gt;\r\n&lt;p&gt;Mỗi th&amp;iacute; sinh chuẩn bị bộ hồ sơ gồm c&amp;aacute;c loại giấy tờ sau đ&amp;acirc;y:&lt;/p&gt;\r\n&lt;p&gt;1. Giấy b&amp;aacute;o tr&amp;uacute;ng tuyển (nhận tại Ph&amp;ograve;ng Đ&amp;agrave;o tạo nếu nhập học trực tiếp); đối với th&amp;iacute; sinh x&amp;eacute;t tuyển theo h&amp;igrave;nh THPT c&amp;oacute; thể nhận giấy b&amp;aacute;o tr&amp;uacute;ng tuyển ngay sau khi c&amp;oacute; kết quả THPT;&lt;/p&gt;\r\n&lt;p&gt;2. Bản sao c&amp;ocirc;ng chứng Bằng tốt nghiệp THPT hay BTVHTH. Th&amp;iacute; sinh tốt nghiệp THPT năm 2020 m&amp;agrave; chưa c&amp;oacute; Bằng tốt nghiệp THPT th&amp;igrave; nộp bản ch&amp;iacute;nh Giấy chứng nhận tốt nghiệp THPT tạm thời;&lt;/p&gt;\r\n&lt;p&gt;3. Bản sao c&amp;ocirc;ng chứng Học bạ THPT;&lt;/p&gt;\r\n&lt;p&gt;4. Bản ch&amp;iacute;nh Giấy chứng nhận kết quả thi THPT năm 2020 (đối với th&amp;iacute; sinh tốt nghiệp THPT năm 2020;&lt;/p&gt;\r\n&lt;p&gt;5. Bản sao Giấy khai sinh; bản sao Hộ khẩu; 03 bản photo CMND; 02 ảnh thẻ cỡ 3x4 chụp trong v&amp;ograve;ng 6 th&amp;aacute;ng; thẻ Bảo hiểm y tế;&lt;/p&gt;\r\n&lt;p&gt;6. Bản sao c&amp;ocirc;ng chứng c&amp;aacute;c giấy tờ li&amp;ecirc;n quan đến đối tượng x&amp;eacute;t tuyển thẳng;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;7. Bản sao c&amp;ocirc;ng chứng c&amp;aacute;c giấy tờ ưu ti&amp;ecirc;n kh&amp;aacute;c (Nếu c&amp;oacute;).&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;8. Đối với trường hợp l&amp;agrave; Nam sinh vi&amp;ecirc;n th&amp;igrave; bắt buộc nộp Giấy di chuyển qu&amp;acirc;n sự, đăng k&amp;yacute; vắng mặt qu&amp;acirc;n sự v&amp;agrave; đăng k&amp;yacute; nghĩa vụ qu&amp;acirc;n sự&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;9.Hồ sơ v&amp;agrave; giấy chuyển sinh hoạt Đảng/Đo&amp;agrave;n.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;h3&gt;II. C&amp;Aacute;C BƯỚC THỰC HIỆN NHẬP HỌC&lt;/h3&gt;\r\n&lt;p&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;&lt;strong&gt;1. NHẬP HỌC TRỰC TUYẾN&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;BƯỚC 1: X&amp;Aacute;C NHẬN NHẬP HỌC&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Th&amp;iacute; sinh đăng nhập trang tin điện tử &lt;a href=&quot;http://vku.udn.vn/nhaphoc2020/&quot;&gt;&lt;strong&gt;http://vku.udn.vn/nhaphoc2020/&lt;/strong&gt;&lt;/a&gt; bằng số CMND của m&amp;igrave;nh, để x&amp;aacute;c nhận nhập học, cụ thể:&lt;/p&gt;\r\n&lt;p&gt;o Thời gian x&amp;aacute;c nhận nhập học: &lt;strong&gt;Từ ng&amp;agrave;y 10/09/2020 đến ng&amp;agrave;y 12/09/2020&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;- Hồ sơ x&amp;aacute;c nhận gồm (Thời hạn t&amp;iacute;nh theo dấu bưu điện, c&amp;oacute; thể đến trường nộp trực tiếp):&lt;/p&gt;\r\n&lt;p&gt;o Đối với th&amp;iacute; sinh tốt nghiệp trước năm 2020: &lt;strong&gt;học bạ (bản gốc)&lt;/strong&gt; v&amp;agrave; photo c&amp;ocirc;ng chứng bằng tốt nghiệp&lt;/p&gt;\r\n&lt;p&gt;o Đối với th&amp;iacute; sinh tốt nghiệp năm 2020: giấy chứng nhận kết quả thi tốt nghiệp THPT năm 2020 v&amp;agrave; bản gốc học bạ. Ri&amp;ecirc;ng đối với th&amp;iacute; sinh thi tốt nghiệp THPT năm 2020 đợt 2, nếu trước ng&amp;agrave;y 12/9 th&amp;iacute; sinh chưa nhận được giấy chứng nhận kết quả thi TN THPT th&amp;igrave; c&amp;oacute; thể x&amp;aacute;c nhận bằng học bạ (bản gốc) v&amp;agrave; phiếu đăng k&amp;yacute; nhập học (theo mẫu tại: &lt;a href=&quot;http://tuyensinh.vku.udn.vn/phieu_nhap_hoc.pdf&quot;&gt;http://tuyensinh.vku.udn.vn/phieu_nhap_hoc.pdf&lt;/a&gt;); Nộp giấy chứng nhận kết quả thi cho Trường ngay khi nhận được để được c&amp;ocirc;ng nhận tr&amp;uacute;ng tuyển.&lt;/p&gt;\r\n&lt;p&gt;- C&amp;aacute;c bước x&amp;aacute;c nhận nhập học:&lt;/p&gt;\r\n&lt;p&gt;o Bước 1: Th&amp;iacute; sinh v&amp;agrave;o trang web tại địa chỉ: &lt;a href=&quot;http://vku.udn.vn/nhaphoc2020&quot;&gt;http://vku.udn.vn/nhaphoc2020&lt;/a&gt;, chọn v&amp;agrave; click n&amp;uacute;t &amp;ldquo;&lt;strong&gt;X&amp;aacute;c nhận ngay&lt;/strong&gt;&amp;rdquo; tương ứng phương thức tr&amp;uacute;ng tuyển.&lt;/p&gt;\r\n&lt;p&gt;o Bước 2: Nhập số chứng minh v&amp;agrave; ng&amp;agrave;y sinh, chọn đối tượng tốt nghiệp THPT, nhập m&amp;atilde; x&amp;aacute;c minh v&amp;agrave; click n&amp;uacute;t &amp;ldquo;&lt;strong&gt;Tiếp tục&lt;/strong&gt;&amp;rdquo;.&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;o Bước 3: Chụp h&amp;igrave;nh v&amp;agrave; chọn h&amp;igrave;nh up theo hướng dẫn.&lt;/p&gt;\r\n&lt;p&gt;o Bước 4: Click n&amp;uacute;t &amp;ldquo;&lt;strong&gt;Upload &amp;hellip;&lt;/strong&gt;&amp;rdquo; v&amp;agrave; sau đ&amp;oacute; click &amp;ldquo;&lt;strong&gt;Ho&amp;agrave;n th&amp;agrave;nh x&amp;aacute;c nhận&lt;/strong&gt;&amp;rdquo;&lt;/p&gt;\r\n&lt;p&gt;o Bước 5: Gửi hồ sơ x&amp;aacute;c nhận nhập học đến Trường. (&lt;strong&gt;Nộp trực tiếp hoặc gửi qua bưu điện&lt;/strong&gt;)&lt;/p&gt;\r\n&lt;p&gt;Sau khi nhận được hồ sơ th&amp;iacute; sinh gửi qua bưu điện, Nh&amp;agrave; trường sẽ cập nhật trạng th&amp;aacute;i hồ sơ x&amp;aacute;c nhận trực tuyến. Th&amp;iacute; sinh đăng nhập v&amp;agrave;o hệ thống x&amp;aacute;c nhận sẽ xuất hiện trạng th&amp;aacute;i &amp;ldquo;&lt;strong&gt;NH&amp;Agrave; TRƯỜNG Đ&amp;Atilde; NHẬN ĐƯỢC MINH CHỨNG X&amp;Aacute;C NHẬN NHẬP HỌC CỦA BẠN.&lt;/strong&gt;&amp;rdquo;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;BƯỚC 2: NHẬP TH&amp;Ocirc;NG TIN V&amp;Agrave; NỘP HỒ SƠ TRỰC TUYẾN&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Từ ng&amp;agrave;y 10/09 đến trước &lt;strong&gt;17 giờ 00&lt;/strong&gt; ng&amp;agrave;y &lt;strong&gt;15/9/2020&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Th&amp;iacute; sinh đăng nhập trang tin điện tử &lt;a href=&quot;http://vku.udn.vn/nhaphoc2020/&quot;&gt;http://vku.udn.vn/nhaphoc2020/&lt;/a&gt; để xem Hướng dẫn chi tiết về điền th&amp;ocirc;ng tin sinh vi&amp;ecirc;n v&amp;agrave; nộp hồ sơ.&lt;/p&gt;\r\n&lt;p&gt;Hồ sơ cần nộp trực tuyến l&amp;agrave;:&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;- Bản sao giấy khai sinh&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;- Bản sao hộ khẩu (Chụp h&amp;igrave;nh 04 trang gồm trang b&amp;igrave;a v&amp;agrave; c&amp;aacute;c trang c&amp;oacute; th&amp;ocirc;ng tin ba, mẹ, v&amp;agrave; bản th&amp;acirc;n)&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;BƯỚC 3: NỘP TIỀN&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Từ ng&amp;agrave;y 10/09 đến trước 17 giờ 00 ng&amp;agrave;y 15/9/2020&lt;/p&gt;\r\n&lt;p&gt;Tổng số tiền nhập học tạm nộp:&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;1. Ng&amp;agrave;nh C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; C&amp;ocirc;ng nghệ kỹ thuật m&amp;aacute;y t&amp;iacute;nh:&lt;/strong&gt; 6.416.805 đồng, gồm c&amp;aacute;c khoản:&lt;/p&gt;\r\n&lt;p&gt;a. Học ph&amp;iacute; học kỳ 1 năm học 2020-2021&lt;/p&gt;\r\n&lt;p&gt;b. Bảo hiểm y tế (3 th&amp;aacute;ng 10,11,12 năm 2020)&lt;/p&gt;\r\n&lt;p&gt;c. Lệ ph&amp;iacute; kh&amp;aacute;m sức khỏe&lt;/p&gt;\r\n&lt;p&gt;d. Kiểm tra tiếng Anh đầu v&amp;agrave;o&lt;/p&gt;\r\n&lt;p&gt;e. Bảo hiểm th&amp;acirc;n thể (tự nguyện)&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;2. Ng&amp;agrave;nh Quản trị kinh doanh:&lt;/strong&gt; 5.930.805 đồng, gồm c&amp;aacute;c khoản:&lt;/p&gt;\r\n&lt;p&gt;a. Học ph&amp;iacute; học kỳ 1 năm học 2020-2021&lt;/p&gt;\r\n&lt;p&gt;b. Bảo hiểm y tế (3 th&amp;aacute;ng 10,11,12 năm 2020)&lt;/p&gt;\r\n&lt;p&gt;c. Lệ ph&amp;iacute; kh&amp;aacute;m sức khỏe&lt;/p&gt;\r\n&lt;p&gt;d. Kiểm tra tiếng Anh đầu v&amp;agrave;o&lt;/p&gt;\r\n&lt;p&gt;e. Bảo hiểm th&amp;acirc;n thể (tự nguyện)&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;3. Tổng số tiền nhập học tạm nộp chuyển về:&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;T&amp;ecirc;n T&amp;agrave;i khoản: &lt;strong&gt;TRƯỜNG ĐẠI HỌC CNTT V&amp;Agrave; TRUYỀN TH&amp;Ocirc;NG VIỆT &amp;ndash; H&amp;Agrave;N&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Số t&amp;agrave;i khoản: &lt;strong&gt;2015201003532&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;T&amp;ecirc;n ng&amp;acirc;n h&amp;agrave;ng: &lt;strong&gt;Ng&amp;acirc;n h&amp;agrave;ng N&amp;ocirc;ng nghiệp v&amp;agrave; Ph&amp;aacute;t triển N&amp;ocirc;ng th&amp;ocirc;n Việt Nam &amp;ndash; Chi nh&amp;aacute;nh T&amp;acirc;n Ch&amp;iacute;nh &amp;ndash; Nam Đ&amp;agrave; Nẵng&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;4. Nội dung nộp tiền hoặc chuyển tiền theo c&amp;uacute; ph&amp;aacute;p sau:&lt;/strong&gt; &lt;span style=&quot;text-decoration: underline;&quot;&gt;&lt;strong&gt;AAAA,XXXX,YYYY&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;Trong đ&amp;oacute;:&lt;/p&gt;\r\n&lt;p&gt;- AAAA: Số Chứng minh thư hoặc số căn cước c&amp;ocirc;ng d&amp;acirc;n&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;- XXXX: Họ v&amp;agrave; t&amp;ecirc;n&lt;/p&gt;\r\n&lt;p&gt;- YYYY: Nhập học 2020&lt;/p&gt;\r\n&lt;p&gt;V&amp;iacute; dụ: 0123456789, Nguyễn Văn A, Nhập học 2020&lt;/p&gt;\r\n&lt;p&gt;Trước &lt;strong&gt;17 giờ 00 ng&amp;agrave;y 15/9/2020&lt;/strong&gt;, th&amp;iacute; sinh tr&amp;uacute;ng tuyển nộp bộ hồ sơ nhập học v&amp;agrave; nộp số tiền cần nộp theo Hướng dẫn n&amp;ecirc;u ở Bước 3.&lt;/p&gt;\r\n&lt;p&gt;Bước 4: Đăng k&amp;yacute; nội tr&amp;uacute; K&amp;yacute; t&amp;uacute;c x&amp;aacute; (nếu th&amp;iacute; sinh c&amp;oacute; nhu cầu)&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;&lt;strong&gt;2. NHẬP HỌC TRỰC TIẾP&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;Khi đến nhập học trực tiếp tại Trường theo th&amp;ocirc;ng b&amp;aacute;o của Nh&amp;agrave; trường &lt;strong&gt;(Dự kiến ng&amp;agrave;y 24-25/09/2020)&lt;/strong&gt;, th&amp;iacute; sinh cần nộp bổ sung hồ sơ đầy đủ gồm c&amp;aacute;c loại giấy tờ được quy định tại mục I.&lt;/p&gt;\r\n&lt;h3&gt;III. MỘT SỐ LƯU &amp;Yacute;&lt;/h3&gt;\r\n&lt;p&gt;1. Số điện thoại hỗ trợ nhập học: 0236 3667 129, 0236 2678 234.&lt;/p&gt;\r\n&lt;p&gt;Group hỗ trợ hỏi đ&amp;aacute;p d&amp;agrave;nh cho T&amp;acirc;n sinh vi&amp;ecirc;n:&amp;nbsp;&lt;a href=&quot;https://www.facebook.com/groups/k20.vku.sict/&quot;&gt;https://www.facebook.com/groups/k20.vku.sict/&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;Fanpage hỗ trợ nhập học: &lt;a href=&quot;http://fb.com/ctsv.vku.udn.vn&quot;&gt;http://fb.com/ctsv.vku.udn.vn&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;2. Th&amp;iacute; sinh dự thi Tốt nghiệp THPT đợt 2 năm 2020 được bảo lưu kết quả x&amp;eacute;t tuyển v&amp;agrave; thực hiện c&amp;aacute;c thủ tục nhập học ngay sau khi c&amp;oacute; kết quả tốt nghiệp THPT 2020&lt;/p&gt;', '', 1, 1, 0, '[\"1\"]', 3991, 99, '2021-01-29 15:33:30', NULL, NULL);
INSERT INTO `cms` VALUES (1099, 123, 'Hướng dẫn điều chỉnh, bổ sung nguyện vọng xét tuyển vào VKU năm 2020', 'Hướng dẫn điều chỉnh, bổ sung nguyện vọng xét tuyển vào VKU năm 2020', 'Hướng dẫn điều chỉnh, bổ sung nguyện vọng xét tuyển vào VKU năm 2020', 'huong-dan-dieu-chinh-bo-sung-nguyen-vong-xet-tuyen-vao-vku-nam-2020', '', 'Hướng dẫn điều chỉnh, bổ sung nguyện vọng xét tuyển vào VKU năm 2020', '', '2020/09/11/poster9_VKU.jpg', 'Sau khi có kết quả thi tốt nghiệp THPT 2020, mỗi thí sinh chỉ được điều chỉnh nguyện vọng đăng ký xét tuyển 1 lần. Do đó, các thí sinh cần phải nắm thật rõ cách thức điều chỉnh nguyện vọng xét tuyển đại học để tăng cơ hội trúng tuyển vào ngành học, trường học phù hợp.', '', '&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;span&gt;Sau khi c&amp;oacute; kết quả thi tốt nghiệp&amp;nbsp;THPT 2020, mỗi th&amp;iacute; sinh chỉ được điều chỉnh nguyện vọng&amp;nbsp;đăng k&amp;yacute; x&amp;eacute;t tuyển 1 lần. Do đ&amp;oacute;, c&amp;aacute;c th&amp;iacute; sinh cần phải nắm thật r&amp;otilde; c&amp;aacute;ch thức điều chỉnh nguyện vọng x&amp;eacute;t tuyển đại học để tăng cơ hội tr&amp;uacute;ng tuyển v&amp;agrave;o ng&amp;agrave;nh học, trường học ph&amp;ugrave; hợp.&lt;br /&gt;&lt;br /&gt;&lt;/span&gt;&lt;strong style=&quot;text-indent: 0.5in;&quot;&gt;&lt;span style=&quot;font-weight: normal;&quot;&gt;C&amp;aacute;c phương thức điều chỉnh nguyện vọng x&amp;eacute;t tuyển đại học 2020&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; vertical-align: baseline;&quot;&gt;&lt;span&gt;Năm 2020, Bộ Gi&amp;aacute;o dục v&amp;agrave; Đ&amp;agrave;o tạo quy định th&amp;iacute; sinh được điều chỉnh nguyện vọng x&amp;eacute;t tuyển theo 2 phương thức: điều chỉnh trực tuyến hoặc bằng Phiếu điều chỉnh nguyện vọng đăng k&amp;yacute;&amp;nbsp;x&amp;eacute;t tuyển. Một điểm đặc biệt m&amp;agrave; c&amp;aacute;c th&amp;iacute; sinh cần lưu &amp;yacute; l&amp;agrave; mỗi bạn chỉ được điều chỉnh đăng k&amp;yacute; x&amp;eacute;t tuyển 1 lần trong thời gian quy định v&amp;agrave; chỉ được sử dụng 1 trong 2 phương thức tr&amp;ecirc;n.&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; background: white; vertical-align: baseline;&quot;&gt;&lt;span&gt;Đối với c&amp;aacute;ch thức&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;mso-bidi-; mso-bidi-theme-font: minor-bidi;&quot;&gt;điều chỉnh nguyện vọng trực tuyến&lt;/span&gt;&lt;/strong&gt;, thời gian dự kiến từ &lt;/span&gt;&lt;span style=&quot;color: red;&quot;&gt;19/9 đến 17g00 ng&amp;agrave;y 25/9.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; background: white; vertical-align: baseline;&quot;&gt;&lt;span&gt;Tuy nhi&amp;ecirc;n, với phương thức&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;mso-bidi-; mso-bidi-theme-font: minor-bidi;&quot;&gt;điều chỉnh nguyện vọng bằng Phiếu điều chỉnh NV&amp;nbsp;đăng k&amp;yacute; x&amp;eacute;t tuyển&lt;/span&gt;&lt;/strong&gt;, thời gian sẽ d&amp;agrave;i hơn 2 ng&amp;agrave;y, dự kiến từ &lt;/span&gt;&lt;span style=&quot;color: red;&quot;&gt;19/9 đến 17g00 ng&amp;agrave;y 27/9.&lt;/span&gt;&lt;span style=&quot;text-align: center; text-indent: 0.5in;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;h3 class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; background: white; vertical-align: baseline;&quot;&gt;&lt;strong style=&quot;text-indent: -0.25in;&quot;&gt;&lt;span&gt;1.&lt;span style=&quot;font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: normal; font-stretch: normal; line-height: normal;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;strong style=&quot;text-indent: -0.25in;&quot;&gt;&lt;span&gt;ĐIỀU CHỈNH NGUYỆN VỌNG TRỰC TUYẾN&lt;/span&gt;&lt;/strong&gt;&lt;/h3&gt;\r\n&lt;p style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; vertical-align: baseline;&quot;&gt;&lt;span&gt;Đối với phương thức n&amp;agrave;y, c&amp;aacute;c thay đổi của th&amp;iacute; sinh chỉ được chấp nhận khi số lượng nguyện vọng sau khi điều chỉnh kh&amp;ocirc;ng lớn hơn số lượng nguyện vọng đ&amp;atilde; đăng k&amp;yacute; ban đầu trong phiếu đăng k&amp;yacute; x&amp;eacute;t tuyển&amp;nbsp;&lt;em&gt;&lt;span&gt;(số nguyện vọng th&amp;iacute; sinh đ&amp;atilde; đăng k&amp;yacute; v&amp;agrave;o thời gian từ 15 &amp;ndash; 30/6).&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; vertical-align: baseline;&quot;&gt;&lt;span&gt;V&amp;iacute; dụ, th&amp;iacute; sinh đăng k&amp;yacute; 4 nguyện vọng trong phiếu đăng k&amp;yacute; x&amp;eacute;t tuyển, khi chỉnh sửa, th&amp;iacute; sinh c&amp;oacute; thể giữ nguy&amp;ecirc;n hoặc &lt;/span&gt;&lt;em style=&quot;mso-bidi-font-style: normal;&quot;&gt;&lt;span style=&quot;color: red;&quot;&gt;giảm bớt số nguyện vọng để bổ sung nguyện vọng v&amp;agrave;o VKU &lt;/span&gt;&lt;/em&gt;&lt;span&gt;v&amp;agrave; thay đổi th&amp;ocirc;ng tin c&amp;aacute;c ng&amp;agrave;nh, c&amp;aacute;c trường m&amp;agrave; m&amp;igrave;nh cảm thấy ph&amp;ugrave; hợp với điểm số nhận được trong kỳ thi tốt nghiệp&amp;nbsp;THPT.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; vertical-align: baseline;&quot;&gt;&lt;strong style=&quot;mso-bidi-font-weight: normal;&quot;&gt;&lt;em style=&quot;mso-bidi-font-style: normal;&quot;&gt;&lt;span style=&quot;color: red;&quot;&gt;Sau đ&amp;acirc;y l&amp;agrave; hướng dẫn cụ thể c&amp;aacute;c bước thay đổi nguyện vọng trực tuyến:&lt;/span&gt;&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;mso-bidi-; mso-bidi-theme-font: minor-bidi; color: black;&quot;&gt;- Bước 1&lt;/span&gt;&lt;/strong&gt;&lt;span&gt;: Th&amp;iacute; sinh truy cập v&amp;agrave;o đường link&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;MsoHyperlink&quot;&gt;&lt;span style=&quot;color: mediumblue; text-decoration: none; text-underline: none;&quot;&gt;&lt;a href=&quot;http://thisinh.thithptquocgia.edu.vn/Account/Login?ReturnUrl=%2f&quot; target=&quot;_blank&quot;&gt;&lt;span style=&quot;color: mediumblue; text-decoration: none; text-underline: none;&quot;&gt;http://thisinh.thithptquocgia.edu.vn&lt;/span&gt;&lt;span style=&quot;text-decoration: none; text-underline: none;&quot;&gt;/&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;&lt;/span&gt;&lt;span&gt;&amp;nbsp;v&amp;agrave; tiến h&amp;agrave;nh đăng nhập.&lt;br /&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599799785_1.jpg&quot; alt=&quot;&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;- Bước 2:&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;&amp;nbsp;Chọn chức năng Thay đổi nguyện vọng đăng k&amp;yacute; x&amp;eacute;t tuyển sinh từ menu Hệ thống.&lt;br /&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599799831_2.jpg&quot; alt=&quot;&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&amp;nbsp;&lt;strong style=&quot;text-indent: 0.5in;&quot;&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;- Bước 3:&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;text-indent: 0.5in; border: 1pt none windowtext; padding: 0in;&quot;&gt;&amp;nbsp;Chọn &amp;ldquo;Chỉnh sửa nguyện vọng&amp;rdquo;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599799859_3.jpg&quot; alt=&quot;&quot; /&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span&gt;Sau khi giao diện &quot;Sửa nguyện vọng&quot; hiển thị, th&amp;iacute; sinh chọn trường, ng&amp;agrave;nh học v&amp;agrave; tổ hợp m&amp;ocirc;n cần thay đổi.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599799893_4.jpg&quot; alt=&quot;&quot; /&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;Giao diện sau khi chỉnh sửa nguyện vọng&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599799916_5.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;Để x&amp;oacute;a bớt một nguyện vọng, th&amp;iacute; sinh nhấn n&amp;uacute;t &amp;ldquo;X&amp;oacute;a&amp;rdquo; tr&amp;ecirc;n m&amp;agrave;n h&amp;igrave;nh Th&amp;ocirc;ng tin đăng k&amp;yacute; nguyện vọng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599799939_6.jpg&quot; alt=&quot;&quot; /&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;L&amp;uacute;c n&amp;agrave;y, cửa sổ pop-up sẽ xuất hiện, bạn nhấn n&amp;uacute;t &amp;ldquo;&lt;/span&gt;&lt;strong style=&quot;text-indent: 0.5in;&quot;&gt;Đồng &amp;yacute;&lt;/strong&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;&amp;rdquo; để x&amp;oacute;a hoặc n&amp;uacute;t &amp;ldquo;&lt;/span&gt;&lt;strong style=&quot;text-indent: 0.5in;&quot;&gt;Hủy&lt;/strong&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;&amp;rdquo; để hủy y&amp;ecirc;u cầu.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599799962_7.jpg&quot; alt=&quot;&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;Với trường hợp muốn thay đổi thứ tự ưu ti&amp;ecirc;n, th&amp;iacute; sinh nhấn n&amp;uacute;t mũi t&amp;ecirc;n để di chuyển nguyện vọng l&amp;ecirc;n xuống.&lt;br /&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599799994_8.jpg&quot; alt=&quot;&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;color: red;&quot;&gt;&amp;nbsp;&lt;span&gt;Với trường hợp bổ sung th&amp;ecirc;m nguyện vọng x&amp;eacute;t tuyển&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;span&gt;- Trước ti&amp;ecirc;n th&amp;iacute; sinh phải x&amp;oacute;a bớt 1 nguyện vọng kh&amp;ocirc;ng cần thiết, khi đ&amp;oacute; n&amp;uacute;t &amp;ldquo;Th&amp;ecirc;m nguyện vọng&amp;rdquo; sẽ hiện ra&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;span&gt;- Th&amp;iacute; sinh điền c&amp;aacute;ch th&amp;ocirc;ng tin: Thứ tự NV, Trường, Ng&amp;agrave;nh v&amp;agrave; Tổ hợp m&amp;ocirc;n như h&amp;igrave;nh sau&lt;br /&gt;&lt;/span&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599800063_9.jpg&quot; alt=&quot;&quot; /&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;- Bước 4:&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;&amp;nbsp;Sau khi điều chỉnh xong v&amp;agrave; kiểm tra, nhấn n&amp;uacute;t Lưu th&amp;ocirc;ng tin sẽ đến bước X&amp;aacute;c nhận đăng k&amp;yacute; (th&amp;iacute; sinh dung số điện thoại đ&amp;atilde; đăng k&amp;yacute; tr&amp;ecirc;n hệ thống v&amp;agrave; soạn 01 tin nhắn &lt;/span&gt;&lt;strong&gt;&lt;span style=&quot;color: red;&quot;&gt;TSO &amp;lt;Số b&amp;aacute;o danh&amp;gt; gửi đến số 6058&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;Hệ thống sẽ gửi một m&amp;atilde; x&amp;aacute;c thực OTP về điện thoại cho th&amp;iacute; sinh. Th&amp;iacute; sinh nhập m&amp;atilde; x&amp;aacute;c thực n&amp;agrave;y v&amp;agrave;o m&amp;agrave;n h&amp;igrave;nh &amp;ldquo;X&amp;aacute;c nhận đăng k&amp;yacute;&amp;rdquo;.&lt;br /&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599800097_10.jpg&quot; alt=&quot;&quot; /&gt;&lt;span style=&quot;text-indent: 0.5in;&quot;&gt;&lt;br /&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;- Bước 5:&lt;/span&gt;&lt;/strong&gt;&lt;span&gt;&amp;nbsp;Sau khi bấm &amp;ldquo;X&amp;aacute;c nhận đăng k&amp;yacute;&amp;rdquo; hệ thống y&amp;ecirc;u cầu x&amp;aacute;c thực một lần nữa xem bạn c&amp;oacute; chắc chắn thực hiện kh&amp;ocirc;ng.&lt;br /&gt;&lt;/span&gt;&lt;/span&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-align: -webkit-center; text-indent: 0px; margin-left: auto; margin-right: auto;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599800123_11.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;Bấm lệnh&lt;strong&gt;&amp;nbsp;&amp;ldquo;OK&amp;rdquo;&lt;/strong&gt;&amp;nbsp;để lưu dữ liệu thay đổi v&amp;agrave;o hệ thống, bấm&amp;nbsp;&lt;strong&gt;&amp;ldquo;Cancel&amp;rdquo;&lt;/strong&gt;&amp;nbsp;để hủy bỏ chỉnh sửa.&lt;/span&gt;&lt;/p&gt;\r\n&lt;h3 class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;strong style=&quot;text-indent: -0.25in;&quot;&gt;&lt;span&gt;2. ĐIỀU CHỈNH BẰNG PHIẾU ĐIỀU CHỈNH NGUYỆN VỌNG ĐĂNG K&amp;Yacute;&amp;nbsp;X&amp;Eacute;T TUYỂN&lt;/span&gt;&lt;/strong&gt;&lt;/h3&gt;\r\n&lt;p style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; vertical-align: baseline;&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;span&gt;Đối với những th&amp;iacute; sinh muốn đăng k&amp;yacute; th&amp;ecirc;m nguyện vọng, c&amp;aacute;c bạn buộc phải sử dụng Phiếu điều chỉnh nguyện vọng&amp;nbsp;đăng k&amp;yacute; x&amp;eacute;t tuyển. &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;Để điều chỉnh nguyện vọng, th&amp;iacute; sinh phải ho&amp;agrave;n thiện 2 tờ phiếu. Phiếu số 1 sẽ do nơi tiếp nhận lưu, phiếu số 2 do th&amp;iacute; sinh giữ. Cạnh đ&amp;oacute;, th&amp;iacute; sinh cũng phải bổ sung lệ ph&amp;iacute; cho số nguyện vọng đăng k&amp;iacute; th&amp;ecirc;m theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-align: center; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot; align=&quot;center&quot;&gt;&lt;img class=&quot;img-responsive&quot; style=&quot;box-sizing: border-box; vertical-align: middle; display: block; max-width: 100%; height: auto; color: #333333; text-indent: 0px;&quot; src=&quot;http://vku.udn.vn/uploads/2020/09/11/1599800184_12.jpg&quot; alt=&quot;&quot; /&gt;&lt;em&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;Mẫu phiếu điều chỉnh nguyện vọng x&amp;eacute;t tuyển&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;C&amp;aacute;c th&amp;iacute; sinh phải điền đầy đủ th&amp;ocirc;ng tin trong 2 phiếu, cụ thể c&amp;aacute;c bước như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;- Bước 1&lt;/span&gt;&lt;/strong&gt;&lt;span&gt;: Điền th&amp;ocirc;ng tin c&amp;aacute; nh&amp;acirc;n đầy đủ v&amp;agrave; ch&amp;iacute;nh x&amp;aacute;c. C&amp;aacute;c bạn th&amp;iacute; sinh cần phải lưu &amp;yacute; rằng th&amp;ocirc;ng tin phải thống nhất với phiếu đăng k&amp;yacute; dự thi tốt nghiệp THPT trước đ&amp;oacute; đ&amp;atilde; đăng k&amp;yacute;.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;- Bước 2:&lt;/span&gt;&lt;/strong&gt;&lt;span&gt;&amp;nbsp;Mục &quot;Đề nghị điều chỉnh chế độ ưu ti&amp;ecirc;n&quot;:&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;span&gt;&amp;nbsp;+ &lt;span&gt;Nếu th&amp;iacute; sinh cần sửa &amp;ldquo;Khu vực ưu ti&amp;ecirc;n&amp;rdquo; hoặc &amp;ldquo;Đối tượng ưu ti&amp;ecirc;n&amp;rdquo; th&amp;igrave; đ&amp;aacute;nh dấu X v&amp;agrave;o &amp;ocirc; trống đầu ti&amp;ecirc;n.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;span&gt;&amp;nbsp;+ &lt;span&gt;Trong mục &quot;Khu vực ưu ti&amp;ecirc;n tuyển sinh&quot;, bạn cần điền một trong c&amp;aacute;c m&amp;atilde; KV1, KV2-NT, KV2 v&amp;agrave; KV3.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top: 6.0pt; margin-right: 0in; margin-bottom: .0001pt; margin-left: 0in; text-indent: .5in; line-height: normal; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;+ Đối với mục &quot;Đối tượng ưu ti&amp;ecirc;n tuyển sinh&quot;, th&amp;iacute; sinh điền một trong c&amp;aacute;c k&amp;yacute; hiệu từ 01 đến 07. Nếu kh&amp;ocirc;ng thuộc đối tượng ưu ti&amp;ecirc;n th&amp;igrave; để trống.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;border: 1pt none windowtext; padding: 0in;&quot;&gt;- Bước 3:&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;span&gt;Ở bảng &quot;Nội dung c&amp;aacute;c nguyện vọng đăng k&amp;yacute; x&amp;eacute;t tuyển sau điều chỉnh&quot;, th&amp;iacute; sinh thực hiện như sau:&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: baseline;&quot;&gt;&lt;span&gt;&amp;nbsp;&lt;span&gt;+ Điền đầy đủ th&amp;ocirc;ng tin về c&amp;aacute;c nguyện vọng cần chỉnh sửa từ cột 1 đến cột 5.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 0.0001pt; text-indent: 0.5in; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-o