INSERT INTO `cms` VALUES (1121, 185, 'ĐHĐN phát động Giải thưởng Sinh viên nghiên cứu khoa học năm học 2020-2021', 'ĐHĐN phát động Giải thưởng Sinh viên nghiên cứu khoa học năm học 2020-2021', 'ĐHĐN phát động Giải thưởng Sinh viên nghiên cứu khoa học năm học 2020-2021', 'dhdn-phat-dong-giai-thuong-sinh-vien-nghien-cuu-khoa-hoc-nam-hoc-2020-2021', 'huhuhuuh', 'ĐHĐN phát động Giải thưởng Sinh viên nghiên cứu khoa học năm học 2020-2021', 'Sth-xiaolin', 'storage/PostImage/01-02-2021_0634pm.jpg', 'Tiếp nối thành công của Giải thưởng “Sinh viên nghiên cứu khoa học (NCKH) Đại học Đà Nẵng (ĐHĐN)” năm học 2019-2020, Giám đốc ĐHĐN vừa ban hành Kế hoạch số 4390/KH-ĐHĐN ngày 04/12/2020 chính thức phát động Giải thưởng “Sinh viên NCKH ĐHĐN” mùa thứ 02, năm học 2020-2021.', 'hihi', '&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Tiếp nối th&amp;agrave;nh c&amp;ocirc;ng của Giải thưởng &amp;ldquo;Sinh vi&amp;ecirc;n nghi&amp;ecirc;n cứu khoa học (NCKH) Đại học Đ&amp;agrave; Nẵng (ĐHĐN)&amp;rdquo; năm học 2019-2020, Gi&amp;aacute;m đốc ĐHĐN vừa ban h&amp;agrave;nh Kế hoạch số 4390/KH-ĐHĐN ng&amp;agrave;y 04/12/2020 ch&amp;iacute;nh thức ph&amp;aacute;t động Giải thưởng &amp;ldquo;Sinh vi&amp;ecirc;n NCKH ĐHĐN&amp;rdquo; m&amp;ugrave;a thứ 02, năm học 2020-2021.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;&lt;img class=&quot;rao&quot;class=&quot;rao&quot;style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 330px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/12/08/1607419460-if3-20web.png&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Giải thưởng uy t&amp;iacute;n, c&amp;oacute; gi&amp;aacute; trị cao&amp;nbsp;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;tiếp th&amp;ecirc;m động lực cho SV NCKH&amp;nbsp;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;Mục đ&amp;iacute;ch của Giải thưởng nhằm khuyến kh&amp;iacute;ch sinh vi&amp;ecirc;n (SV) tham gia NCKH, ph&amp;aacute;t huy năng lực tư duy s&amp;aacute;ng tạo v&amp;agrave; khởi nghiệp đổi mới s&amp;aacute;ng tạo; n&amp;acirc;ng cao khả năng NCKH độc lập, phương ph&amp;aacute;p l&amp;agrave;m việc nh&amp;oacute;m để g&amp;oacute;p phần h&amp;igrave;nh th&amp;agrave;nh năng lực tự học, tự nghi&amp;ecirc;n cứu cho SV.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;Đ&amp;acirc;y&amp;nbsp;l&amp;agrave; &amp;ldquo;s&amp;acirc;n chơi&amp;rdquo; học thuật uy t&amp;iacute;n, thường ni&amp;ecirc;n cho SV ĐHĐN, qua đ&amp;oacute; tiếp tục ph&amp;aacute;t hiện, bồi dưỡng những &amp;yacute; tưởng, giải ph&amp;aacute;p s&amp;aacute;ng tạo, đ&amp;oacute;ng g&amp;oacute;p giải quyết những vấn đề thực tiễn v&amp;agrave; phục vụ cộng đồng.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot;class=&quot;rao&quot;style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 564px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/12/08/1607419460-kbhd85eff361936e868b127-20-281-29.jpg&quot; alt=&quot;&quot; /&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Gi&amp;aacute;m đốc ĐHĐN Nguyễn Ngọc Vũ&amp;nbsp;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;trao Giải Nhất SV NCKH năm học 2019-2020&amp;nbsp;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;C&amp;aacute;c lĩnh vực x&amp;eacute;t tặng trao Giải thưởng bao gồm: (1) Khoa học Kỹ thuật v&amp;agrave; C&amp;ocirc;ng nghệ, Tự nhi&amp;ecirc;n; (2) Khoa học X&amp;atilde; hội v&amp;agrave; Nh&amp;acirc;n văn; (3) Khoa học Y, Dược: Y học cơ sở, Y học l&amp;acirc;m s&amp;agrave;ng, Dược học, C&amp;ocirc;ng nghệ sinh học trong Y học, Khoa học Y, Dược kh&amp;aacute;c.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;Mỗi đề t&amp;agrave;i do 01 c&amp;aacute; nh&amp;acirc;n hoặc tập thể SV thực hiện (kh&amp;ocirc;ng qu&amp;aacute; 05 SV, trong đ&amp;oacute; phải x&amp;aacute;c định r&amp;otilde; 01 SV chịu tr&amp;aacute;ch nhiệm ch&amp;iacute;nh thuộc đơn vị.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;&lt;img class=&quot;rao&quot;class=&quot;rao&quot;style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 523px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/12/08/1607419461-5281fd1d1a1deb43b20c-20-281-29.jpg&quot; alt=&quot;&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Giải thưởng SV NCKH ĐHĐN nhận được&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;sự hỗ trợ, đồng h&amp;agrave;nh của c&amp;aacute;c doanh nghiệp&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;C&amp;aacute;c đề t&amp;agrave;i c&amp;oacute; b&amp;agrave;i b&amp;aacute;o li&amp;ecirc;n quan được c&amp;ocirc;ng bố tr&amp;ecirc;n c&amp;aacute;c tạp ch&amp;iacute; thuộc danh mục WoS/SCOPUS hoặc đạt giải trong c&amp;aacute;c cuộc thi khoa học v&amp;agrave; c&amp;ocirc;ng nghệ, khởi nghiệp s&amp;aacute;ng tạo cấp Quốc gia,&amp;nbsp;Quốc tế trong năm học 2020-2021 sẽ được đặc c&amp;aacute;ch tham gia x&amp;eacute;t giải thưởng trực tiếp cấp ĐHĐN.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot;class=&quot;rao&quot;style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 1237px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/12/08/1607419461-if1.png&quot; alt=&quot;&quot; /&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Giải thưởng l&amp;agrave; &quot;s&amp;acirc;n chơi&quot; học thuật,&amp;nbsp;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;hun đ&amp;uacute;c t&amp;agrave;i năng, tư duy s&amp;aacute;ng tạo cho SV&amp;nbsp;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;C&amp;aacute;c c&amp;aacute; nh&amp;acirc;n, tập thể c&amp;oacute; th&amp;agrave;nh t&amp;iacute;ch xuất sắc trong phong tr&amp;agrave;o SV NCKH, c&amp;oacute; những c&amp;ocirc;ng tr&amp;igrave;nh, đề t&amp;agrave;i NCKH xuất sắc sẽ được t&amp;ocirc;n vinh, khen thưởng.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;C&amp;aacute;c đề t&amp;agrave;i đạt giải được tặng Giấy khen của Gi&amp;aacute;m đốc ĐHĐN k&amp;egrave;m theo tiền thưởng gồm: Giải Nhất 20 triệu đồng/đề t&amp;agrave;i; Giải Nh&amp;igrave; 15 triệu đồng/đề t&amp;agrave;i; Giải Ba 8 triệu đồng/đề t&amp;agrave;i; Giải khuyến kh&amp;iacute;ch 5 triệu đồng/đề t&amp;agrave;i.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;C&amp;aacute;c t&amp;aacute;c giả/đồng t&amp;aacute;c giả của c&amp;aacute;c đề t&amp;agrave;i được chọn tham gia x&amp;eacute;t tặng Giải thưởng cấp ĐHĐN sẽ được cấp Giấy Chứng nhận.&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot;class=&quot;rao&quot;style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 583px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/12/08/1607419462-5_1.jpg&quot; alt=&quot;&quot; /&gt;&lt;br style=&quot;box-sizing: border-box;&quot; /&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Lan tỏa tinh thần đổi mới s&amp;aacute;ng tạo&amp;nbsp;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;Năm học 2019-2020, nh&amp;oacute;m SV Trường ĐH B&amp;aacute;ch khoa-ĐHĐN đ&amp;atilde; vinh dự nhận Giải Nhất Giải thưởng SV NCKH của ĐHĐN với đề t&amp;agrave;i &amp;nbsp;&amp;ldquo;Thiết kế, chế tạo bộ l&amp;agrave;m m&amp;aacute;t cho tấm pin năng lượng mặt trời nhằm n&amp;acirc;ng cao hiệu suất chuyển đổi quang điện của tấm pin&amp;rdquo; (&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Xem th&amp;ecirc;m tin&amp;nbsp;&lt;a style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: 20px; font-family: inherit; vertical-align: baseline; color: #0c7ed7; text-decoration-line: none; outline: 0px; background-color: transparent; transition: color 0.3s linear 0s;&quot; href=&quot;http://udn.vn/tin-tuc/chi-tiet/danh-muc/ti%C3%AAu-%C4%91i%E1%BB%83m-sinh-vi%C3%AAn/sinh-vien-truong-dai-hoc-bach-khoa-dai-hoc-da-nang-nghien-cuu-thiet-ke-che-tao-bo-lam-mat-cho-tam-pin-nang-luong-mat-troi&quot;&gt;tại đ&amp;acirc;y&lt;/a&gt;&lt;/strong&gt;);&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;02 Giải Nh&amp;igrave; thuộc về nh&amp;oacute;m SV Trường ĐH Kinh tế-ĐHĐN&amp;nbsp;v&amp;agrave; nh&amp;oacute;m SV Trường ĐH Sư phạm Kỹ thuật-ĐHĐN;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: justify;&quot;&gt;03 Giải Ba thuộc về c&amp;aacute;c nh&amp;oacute;m SV Trường ĐH Sư phạm, Trường ĐH Ngoại ngữ v&amp;agrave; Khoa Y Dược-ĐHĐN.&amp;nbsp;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Xem th&amp;ecirc;m tin&lt;a style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: 20px; font-family: inherit; vertical-align: baseline; color: #0c7ed7; text-decoration-line: none; outline: 0px; background-color: transparent; transition: color 0.3s linear 0s;&quot; href=&quot;http://udn.vn/tin-tuc/tin-tuc-su-kien/chi-tiet/dai-hoc-da-nang-to-chuc-festival-khoa-hoc-cong-nghe-sinh-vien-lan-thu-2-nam-2020-trao-thuong-va-phat-dong-giai-thuong-sinh-vien-nghien-cuu-khoa-hoc&quot;&gt;&amp;nbsp;tại đ&amp;acirc;y&lt;/a&gt;.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;box-sizing: border-box; margin: 10px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 15px; line-height: 24px; font-family: Roboto-Regular; vertical-align: baseline; color: #333333; text-align: center;&quot;&gt;&lt;img class=&quot;rao&quot;class=&quot;rao&quot;style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border-style: none; font: inherit; vertical-align: middle; max-width: 100%; width: 875px; height: 580px;&quot; title=&quot;&quot; src=&quot;http://vku.udn.vn//uploads/2020/12/08/1607419462-khff94ba769175602b3964_1.jpg&quot; alt=&quot;&quot; /&gt;&lt;br style=&quot;box-sizing: border-box;&quot; /&gt;&lt;em style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;&quot;&gt;Ph&amp;aacute;t triển &amp;yacute; tưởng, giải ph&amp;aacute;p&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;